import { AfterViewInit, Directive, ElementRef, EventEmitter, Input, OnInit, Output, ViewChild } from '@angular/core';
import { AbstractTreeListboxRendererComponent } from './renderer/abstract-tree-listbox-renderer.component';
import { StylesUtilService } from '../utilities/styles.util.service';
import { AbstractListBox } from './abstract-listbox.component';
import { Observable } from 'rxjs';

export class TreeListBoxElement<T> {
	public nodeData: T;
	public level: number;
	public selected: boolean;

	constructor(pNodeData?: T, pLevel?: number, pSelected?: boolean) {
		this.nodeData = pNodeData;
		this.level = pLevel;
		this.selected = pSelected;
	}
}

@Directive()
export abstract class AbstractApiTreeListBox<T> extends AbstractListBox<TreeListBoxElement<T>> implements OnInit, AfterViewInit {

	public columnDefs: Array<any>;
	public treeValues: Array<TreeListBoxElement<T>> = [];
	@ViewChild('hidden', {static: true}) public override hiddenElement: ElementRef;

	@Input() public updateHierarchy = true;

	public _selectedTreeItem: TreeListBoxElement<T>;

	@Input()
	set selectedTreeItem(value: TreeListBoxElement<T>) {
		this._selectedTreeItem = value;
		this.selectTreeItemInGrid();
	}

	get selectedTreeItem() {
		return this._selectedTreeItem;
	}

	@Output() selectedTreeItemChange = new EventEmitter<TreeListBoxElement<T>>();

	protected _selectedIDList: string;

	@Input()
	set selectedIDList(value: string) {
		this._selectedIDList = value;
		if (!value) {
			this.initSelectionList();
		}
		this.selectedIDListChange.emit(this._selectedIDList);
	}

	get selectedIDList() {
		this._selectedIDList = '';
		for (const selectedItem of this.multipleSelectedItemList) {
			if (this._selectedIDList && this._selectedIDList !== '') {
				this._selectedIDList += ',';
			}
			const level = selectedItem['level'] === 0 ? 0 : 1;
			this._selectedIDList += this.getSelectionPrefix(level) + selectedItem.nodeData[this.getIdField(level)];
		}
		return this._selectedIDList;
	}

	@Output() public selectedIDListChange = new EventEmitter<string>();

	public paddingSingleSelection = 0;

	protected constructor() {
		super();
	}

	protected abstract getData(): Observable<Array<T>>;

	protected abstract getSelectionPrefix(level: number): string;

	public override ngOnInit() {
		this.configGrid();
	}

	protected configGrid() {
		this.paddingSingleSelection = this.multipleSelection ? 0 : 2;
		this.gridOptions = {};
		this.gridOptions.headerHeight = 0;
		this.gridOptions.rowSelection = 'single';
		const lineHeight = StylesUtilService.getStyleValue(this.hiddenElement, 'line-height');
		if (lineHeight) {
			this.gridOptions.rowHeight = Number(lineHeight);
		} else {
			this.gridOptions.rowHeight = Number(26);
		}
		this.gridOptions.suppressCellSelection = true;

		if (this.multipleSelection) {
			this.gridOptions.suppressRowClickSelection = true;
		} else {
			this.gridOptions.suppressRowClickSelection = this.isDisabled;
		}

		this.columnDefs = [
			{
				colId:                 'id',
				cellRendererFramework: AbstractTreeListboxRendererComponent,
				cellRendererParams:    {
					changeFunction:         (e) => {
						this.changeValues(e);
					},
					level0IDField:          this.getIdField(0),
					level0DescriptionField: this.getDescriptionField(0),
					level1IDField:          this.getIdField(1),
					level1DescriptionField: this.getDescriptionField(1),
					isDisabled:             this.isDisabled,
					isMultipleSelection:    this.multipleSelection
				}
			}
		];

		this.gridOptions.getRowNodeId =
			(item) => {
				if (item.nodeData[this.getIdField(1)]) {
					return item.level + '-' + item.nodeData[this.getIdField(1)];
				} else {
					return null;
				}
			};

		this.gridOptions.columnDefs = this.columnDefs;

	}

	public ngAfterViewInit() {
		this.getRows();
	}

	protected getRows(): void {
		this.getData()
			.subscribe(
				(dataVector: Array<T>) => {
					this.loadValues(dataVector);
					this.gridOptions.api.hideOverlay();
					this.gridOptions.api.setRowData(this.treeValues);
					this.gridOptions.api.redrawRows();
					if (this.multipleSelection) {
						this.initSelectionList();
					} else if (this.selectedTreeItem) {
						this.selectTreeItemInGrid();
					}
					this.gridOptions.api.doLayout();
					this.gridOptions.api.sizeColumnsToFit();
				},
				() => {
					this.gridOptions.api.hideOverlay();
				}
			);
	}

	protected loadValues(dataVector: Array<T>) {
		this.treeValues = [];
		let previousParent: number | string;

		dataVector.forEach(element => {
			if (!previousParent || element[this.getIdField(0)] !== previousParent) {
				previousParent = element[this.getIdField(0)];
				const parentNode = new TreeListBoxElement(element, 0, false);
				this.treeValues.push(parentNode);
			}
			const node = new TreeListBoxElement(element, 1, false);
			this.treeValues.push(node);
		});
	}

	protected initSelectionList(): void {
		if (this.multipleSelection) {
			if (this._selectedIDList) {
				const selectedIDStringList: Array<string> = this._selectedIDList.split(',');
				selectedIDStringList.forEach(selectedID => {
					if (selectedID.startsWith(this.getSelectionPrefix(0))) {
						this.treeValues.forEach(element => {
							if (element.level === 0 && ((element.nodeData[this.getIdField(0)] + '') === selectedID.substring(1, selectedID.length))) {
								element.selected = true;
								this.addSelectedItem(element);
							}
						});
					}
					if (selectedID.startsWith(this.getSelectionPrefix(1))) {
						this.treeValues.forEach(element => {
							if (element.level === 1 && ((element.nodeData[this.getIdField(1)] + '') === selectedID.substring(1, selectedID.length))) {
								element.selected = true;
								this.addSelectedItem(element);
							}
						});
					}
				});
			} else {
				this.multipleSelectedItemList = [];
				this.cleanSelection();
			}
		}
	}

	// Override
	public override cleanSelection(): void {
		this.treeValues = this.treeValues.map(treeValue => {
			treeValue.selected = false;
			return treeValue;
		});
		if (this.gridOptions && this.gridOptions.api) {
			this.gridOptions.api.redrawRows();
		}
	}

	public override doClick(row: any) {
		if (!this.multipleSelection && !this.isDisabled) {
			this.selectedTreeItem = row.node.data;
			this.selectedTreeItemChange.emit(row.node.data);
		}
	}

	public changeValues(event: any) {
		if (this.multipleSelection) {
			this.addRemoveToMultipleSelectedItem(event);
			if (this.updateHierarchy) {
				if (event.level === 0) {
					this.selectUnselectChildTree(event);
				} else {
					this.selectUnselectParentTree(event);
				}
			}
			this.selectedIDListChange.emit(this.selectedIDList);
		}
		if (this.gridOptions.api) {
			this.gridOptions.api.doLayout();
			this.gridOptions.api.sizeColumnsToFit();
		}
	}

	private selectUnselectChildTree(event: any) {
		this.treeValues.forEach((value: TreeListBoxElement<T>) => {
			if (value.nodeData[this.getIdField(0)] === event.nodeData[this.getIdField(0)]) {
				value.selected = event.selected;
				this.addRemoveToMultipleSelectedItem(value);
			}
		});
	}

	private selectUnselectParentTree(event: any) {
		const parentID = event.nodeData[this.getIdField(0)];
		let allChildSelected = true;
		let anyNode = false;
		this.treeValues.forEach((value: TreeListBoxElement<T>) => {
			if (value.nodeData[this.getIdField(0)] === parentID) {
				anyNode = true;
				if (!value.selected && value.level === 1) {
					allChildSelected = false;
				}
			}
		});
		if (anyNode) {
			this.treeValues.forEach((value: TreeListBoxElement<T>) => {
				if (value.level === 0 && value.nodeData[this.getIdField(0)] === parentID) {
					value.selected = allChildSelected;
					this.addRemoveToMultipleSelectedItem(value);
				}
			});
		}
	}

	private addRemoveToMultipleSelectedItem(event: any) {
		if (this.multipleSelectedItemList && this.multipleSelectedItemList !== undefined) {
			const elementIndexInSelectedList: number = this.multipleSelectedItemList.findIndex((item) => {
				return (item.nodeData[this.getIdField(1)] === event.nodeData[this.getIdField(1)] && item['level'] === event['level']);
			});
			if (elementIndexInSelectedList < 0 && event.selected) {
				let newElement: TreeListBoxElement<T>;
				newElement = new TreeListBoxElement(event.nodeData, event['level'], event['selected']);
				this.multipleSelectedItemList.push(newElement);
			} else {
				if (elementIndexInSelectedList !== -1 && !event.selected) {
					this.multipleSelectedItemList.splice(elementIndexInSelectedList, 1);
					this.multipleSelectedItemList = this.multipleSelectedItemList.slice();
				}
			}
		} else {
			this.multipleSelectedItemList = [];
			let newElement: TreeListBoxElement<T>;
			newElement = new TreeListBoxElement(event.nodeData, event['level'], event['selected']);
			this.multipleSelectedItemList.push(newElement);
		}
	}

	public addSelectedItem(selected: TreeListBoxElement<T>) {
		if (this.containsElement(selected)) {
			this.removeElement(selected);
		} else {
			if (!this.multipleSelectedItemList) {
				this.multipleSelectedItemList = [selected];
			} else {
				this.multipleSelectedItemList.push(selected);
			}
		}
	}

	protected selectTreeItemInGrid() {
		if (this.gridOptions && this.gridOptions.api) {
			this.gridOptions.api.forEachNode(node => {
				if (!this.multipleSelection) {
					if (!this.selectedTreeItem && this.selectFirstItem) {
						if (node.rowIndex === 0) {
							node.setSelected(true);
							this.selectedTreeItem = node.data;
							this.selectedTreeItemChange.emit(node.data);
							return;
						}
					} else if (this.selectedTreeItem && this.selectedTreeItem.nodeData) {
						if (node.data.nodeData[this.getIdField(this.selectedTreeItem.level)] === this.selectedTreeItem.nodeData[this.getIdField(this.selectedTreeItem.level)] && node.data.level === this.selectedTreeItem.level) {
							node.setSelected(true);
							return;
						}
					}
				}
			});
		}
	}

	public override onModelUpdated(pEvent: any) {
	}

	public removeElement(seleccionado: TreeListBoxElement<T>) {

		for (let i = 0; i < this.multipleSelectedItemList.length; i++) {
			const element = this.multipleSelectedItemList[i];
			if (element.nodeData[this.getIdField(1)] === seleccionado.nodeData[this.getIdField(1)] && element['level'] === seleccionado['level']) {
				this.multipleSelectedItemList.splice(i, 1);
				return;
			}
		}
	}

	public containsElement(seleccionado: TreeListBoxElement<T>) {
		if (this.multipleSelectedItemList) {
			for (const element of this.multipleSelectedItemList) {
				if (element.nodeData[this.getIdField(1)] === seleccionado.nodeData[this.getIdField(1)] && element['level'] === seleccionado['level']) {
					return true;
				}
			}
		}
		return false;
	}

}

export class TwoListItem {
  constructor(public displayName: string, public colId: string, public isSelected: boolean, public isVisible: boolean) {
  }
}

export class CurrentSelectionStatus {
  constructor(public available: Array<TwoListItem>, public visible: Array<TwoListItem>) {
  }

  public selectAvailable(element: TwoListItem, filteredList: Array<TwoListItem>, isShiftKey: boolean, isControlKey: boolean): void {
    this.clear(this.visible);
    this.select(this.available, element, filteredList, isShiftKey, isControlKey);
  }

  public selectVisible(element: TwoListItem, filteredList: Array<TwoListItem>, isShiftKey: boolean, isControlKey: boolean): void {
    this.clear(this.available);
    this.select(this.visible, element, filteredList, isShiftKey, isControlKey);
  }

  public clearAll(): void {
    this.clear(this.available);
    this.clear(this.visible);
  }

  private select(
    selectedList: Array<TwoListItem>,
    element: TwoListItem,
    filteredList: Array<TwoListItem>,
    isShiftKey: boolean,
    isControlKey: boolean
  ): void {
    if (selectedList.length > 0 && isShiftKey) {
      this.selectRange(selectedList, element, filteredList);
    } else {
      this.selectSingle(selectedList, element, isControlKey);
    }
  }

  private selectRange(selectedList: Array<TwoListItem>, element: TwoListItem, filteredList: Array<TwoListItem>) {
    const indexOfLastSelected = filteredList.indexOf(selectedList[0]);
    const indexOfSelected = filteredList.indexOf(element);

    this.clear(selectedList);

    if (indexOfLastSelected < indexOfSelected) {
      for (let i = indexOfLastSelected; i <= indexOfSelected; i++) {
        filteredList[i].isSelected = true;
        selectedList.push(filteredList[i]);
      }
    } else {
      for (let i = indexOfLastSelected; i >= indexOfSelected; i--) {
        filteredList[i].isSelected = true;
        selectedList.push(filteredList[i]);
      }
    }
  }

  private selectSingle(selectedList: Array<TwoListItem>, element: TwoListItem, isControlKey: boolean) {
    element.isSelected = !element.isSelected;
    if (selectedList.length === 0 || (selectedList.length > 0 && isControlKey)) {
      if (element.isSelected) {
        selectedList.push(element);
      } else {
        selectedList.splice(selectedList.indexOf(element), 1);
      }
    } else {
      this.clear(selectedList);
      if (element.isSelected) {
        selectedList.push(element);
      }
    }
  }

  private clear(list: Array<TwoListItem>) {
    list.forEach(element => element.isSelected = false);
    list.splice(0);
  }
}

import { OnInit, Component, EventEmitter, Input, Output } from '@angular/core';
import { I18nService } from 'systelab-translate';
import { Month } from '../month-selector/month-selector.component';

export class Week {
	constructor(public week: number, public text: string, public day: number, public month: number, public year: number, public isActive: boolean) {
	}
}

@Component({
	selector: 'systelab-week-selector',
	templateUrl: './week-selector.component.html'
})
export class WeekSelectorComponent implements OnInit {

	@Input() public maxDate: Date;
	@Input() public minDate: Date;
	@Input() public currentDate: Date;
	@Input() public disabled: boolean;

	private _selectedWeek: Week;
	@Input()
	get selectedWeek(): Week {
		return this._selectedWeek;
	}

	@Output() public selectedWeekChange = new EventEmitter();

	set selectedWeek(value: Week) {
		this._selectedWeek = value;
		this.selectedWeekChange.emit(this._selectedWeek);
	}

	public weeks: Array<Week> = [];
	public months: Array<Month> = [];
	public isOpened = false;
	public monthNames = [];
	public daysNames = [];

	constructor(private i18nService: I18nService) {
		const year = new Date();
		for (let i = 1; i <= 12; i++) {
			const month = this.i18nService.formatMonthAndYear(new Date(i + '/01/' + year.getFullYear()))
				.split(',');
			this.monthNames.push(month[0]);
		}
		this.daysNames.push(this.i18nService.instant('COMMON_SUNDAY'));
		this.daysNames.push(this.i18nService.instant('COMMON_MONDAY'));
		this.daysNames.push(this.i18nService.instant('COMMON_TUESDAY'));
		this.daysNames.push(this.i18nService.instant('COMMON_WEDNESDAY'));
		this.daysNames.push(this.i18nService.instant('COMMON_THURSDAY'));
		this.daysNames.push(this.i18nService.instant('COMMON_FRIDAY'));
		this.daysNames.push(this.i18nService.instant('COMMON_SATURDAY'));
	}

	public ngOnInit(): void {
		if (!this.currentDate) {
			this.currentDate = new Date();
		}
		this.getMonths();
	}

	public onClicked(e) {
		this.isOpened = true;
	}

	public hideSelector() {
		this.isOpened = false;
	}

	public getMonths() {
		this.months = [];
		const month = this.currentDate.getMonth();
		const year = this.currentDate.getFullYear();
		for (let i = 0; i <= 11; i++) {
			const dateStart = new Date(year, i, 1, 0, 0, 0, 0);
			const dateEnd = new Date(year, i + 1, 0);
			if (this.checkDateIntoIntervals(dateStart) || this.checkDateIntoIntervals(dateEnd)) {
				const isActive = (i === month);
				this.months.push({month: i, year: year, text: this.monthNames[i], isActive: isActive});
			}
		}
		this.getWeeks();
	}

	public getWeeks() {
		this.weeks = [];
		const monthActive = this.months.filter(x => x.month === this.currentDate.getMonth())[0];
		const weekInfo = this.getWeeksInMonth(monthActive.month, monthActive.year);
		const week = this.getWeek(this.currentDate.getFullYear(), this.currentDate.getMonth(), this.currentDate.getDate(), true);
		for (let i = 0; i < weekInfo.length - 1; i++) {
			let isActive = false;
			const dateStart = new Date(monthActive.year, monthActive.month, weekInfo[i].start, 0, 0, 0, 0);
			const dateEnd = new Date(monthActive.year, monthActive.month, weekInfo[i].end, 0, 0, 0, 0);
			if (this.checkDateIntoIntervals(dateStart) || this.checkDateIntoIntervals(dateEnd)) {
				const text = this.i18nService.instant('COMMON_WEEK') + ' ' + weekInfo[i].number + ': ' + this.daysNames[dateStart.getDay()] + ' ' + weekInfo[i].start + ' ' + this.i18nService.instant('COMMON_TO') + ' ' + this.daysNames[dateEnd.getDay()] + ' ' + weekInfo[i].end;
				if (weekInfo[i].number === week) {
					isActive = true;
				}
				const weekObj: Week = new Week(weekInfo[i].number, text, weekInfo[i].start, monthActive.month, monthActive.year, isActive);
				this.weeks.push(weekObj);
				if (isActive) {
					this.selectedWeek = weekObj;
				}
			}

		}
	}

	public onClickedWeek(week: Week) {
		this.currentDate = new Date(week.year, week.month, week.day);
		this.hideSelector();
		this.getWeeks();
	}

	public onClickedMonth(month: Month) {
		this.currentDate = new Date(month.year, month.month, 1);
		this.getMonths();
	}

	public addYear(num: number) {
		const newDate = new Date(this.currentDate.getFullYear() + (num), this.currentDate.getMonth(), this.currentDate.getDate());
		if (this.checkDateIntoIntervals(newDate)) {
			this.currentDate = newDate;
			this.getMonths();
		}
	}

	public addMonth(num: number) {
		const dateStart = new Date(this.currentDate.getFullYear(), this.currentDate.getMonth() + (num), 1);
		const dateEnd = new Date(this.currentDate.getFullYear(), this.currentDate.getMonth() + 1 + (num), 0);
		if (this.checkDateIntoIntervals(dateStart) || this.checkDateIntoIntervals(dateEnd)) {
			this.currentDate = dateStart;
			this.getMonths();
		}
	}

	public checkDateIntoIntervals(date: Date) {
		if (this.minDate && this.maxDate) {
			if (date >= this.minDate && date <= this.maxDate) {
				return true;
			} else {
				return false;
			}
		} else if (this.minDate) {
			if (date >= this.minDate) {
				return true;
			} else {
				return false;
			}
		} else if (this.maxDate) {
			if (date <= this.maxDate) {
				return true;
			} else {
				return false;
			}
		} else {
			return true;
		}
	}

	// TODO: Move to Translate Library
	public getWeeksInMonth(monthActual: number, yearActual: number) {
		const firstDate = new Date(yearActual, monthActual, 1);
		const lastDate = new Date(yearActual, monthActual + 1, 0);
		const numDays = lastDate.getDate();
		const weeks = [];
		let endDay = 7 - firstDate.getDay(), startDay = 1;
		if (firstDate.getDay() === 0) {
			endDay = 1;
		} else {
			endDay = 7 - firstDate.getDay() + 1;
		}
		while (startDay <= numDays) {
			if (startDay === 1) {
				weeks.push({start: startDay, end: endDay, number: this.getWeek(yearActual, monthActual, startDay, true)});
			}
			startDay = endDay + 1;
			endDay = endDay + 7;
			endDay = startDay === 1 && endDay === 8 ? 1 : endDay;
			if (endDay > numDays) {
				endDay = numDays;
			}
			weeks.push({start: startDay, end: endDay, number: this.getWeek(yearActual, monthActual, startDay, true)});
		}
		return weeks;
	}

	// TODO: Move to Translate Library
	public getWeek(yearActual, monthActual, dayActual, isGregorian) {
		monthActual += 1; // use 1-12
		const binaryMod = Math.floor((14 - (monthActual)) / 12); // Only can have as result 1 or 0
		const yearValue = yearActual + 4800 - binaryMod;
		const monthValue = (monthActual) + (12 * binaryMod) - 3;
		// Find JulianDay
		let julianday = 0;
		if (isGregorian) {
			julianday = dayActual + Math.floor(((153 * monthValue) + 2) / 5) +
				(365 * yearValue) + Math.floor(yearValue / 4) - Math.floor(yearValue / 100) +
				Math.floor(yearValue / 400) - 32045;  // (gregorian calendar)
		} else {
			julianday = (dayActual + 1) + Math.round(((153 * monthValue) + 2) / 5) + (365 + yearValue) +
				Math.round(yearValue / 4) - 32083;    // (julian calendar)
		}
		// now calc weekNumber according to Julian Day
		const d4 = (julianday + 31741 - (julianday % 7)) % 146097 % 36524 % 1461;
		const l = Math.floor(d4 / 1460);
		const totalDays = ((d4 - l) % 365) + l;
		const weekNumber = Math.floor(totalDays / 7) + 1;
		return weekNumber;
	}
}

