import { ChangeDetectorRef, Directive, Input, OnDestroy, OnInit, Renderer2 } from '@angular/core';
import { AgRendererComponent } from 'ag-grid-angular';
import { IGetRowsParams } from 'ag-grid-community';
import { AbstractComboBox } from './abstract-combobox.component';
import { Observable } from 'rxjs';
import { PreferencesService } from 'systelab-preferences';

@Directive()
export abstract class AbstractApiComboBox<T> extends AbstractComboBox<T> implements AgRendererComponent, OnInit, OnDestroy {

	public startsWith = '';

	public override params: any;

	public totalItemsLoaded = false;

	constructor(public override myRenderer: Renderer2, public chref: ChangeDetectorRef, public override preferencesService?: PreferencesService) {
		super(myRenderer, chref, preferencesService);
	}

	// override
	protected override configGrid() {

		super.configGrid();
		this.gridOptions.rowModelType = 'infinite';
		this.gridOptions.paginationPageSize = 20;
		this.gridOptions.cacheBlockSize = 20;
		this.gridOptions.cacheOverflowSize = 2;
		this.gridOptions.maxConcurrentDatasourceRequests = 1;
		this.gridOptions.infiniteInitialRowCount = 0;
		this.gridOptions.maxBlocksInCache = 100;

	}

	protected override configGridData() {
		this.gridOptions.datasource = null;
	}

	public abstract getData(page: number, itemsPerPage: number, startsWithParameter: string): Observable<Array<T>>;

	public abstract getTotalItems(): number;

	public override refresh(params: any): boolean {
		if (this.gridOptions && this.gridOptions.api) {
			this.gridOptions.api.setDatasource(this);
		}
		return true;
	}

	// override
	public override loop(): void {
		let result = true;

		if (this.isDropDownOpen()) {
			// First time opened we load the table
			if (this.gridOptions.datasource === null) {
				this.gridOptions.datasource = this;
				this.refresh(null);
			}
			if (this.totalItemsLoaded) {
				this.setDropdownHeight();
				this.setDropdownPosition();
				result = false;
			}
		}
		if (result && this.isDropdownOpened) {
			setTimeout(() => this.loop(), 10);
		} else {
			return;
		}
	}

	//override
	protected override getTotalItemsInCombo(): number {
		return this.getTotalItems();
	}

	public override doSearch(event: any) {
		if (event.shiftKey || event.ctrlKey) {
			return;
		}
		this.startsWith = event.target.value;
		this.refresh(null);
	}

	public getRows(params: IGetRowsParams): void {

		const page: number = params.endRow / this.gridOptions.paginationPageSize;
		const pageSize: number = this.gridOptions.paginationPageSize;

		const emptyElemNumber: number = this.emptyElement ? 1 : 0;
		const allNumber: number = this.allElement ? 1 : 0;
		const totalItems: number = this.getTotalItems() + emptyElemNumber + allNumber;
		const modulus: number = totalItems % pageSize;

		if (page === 1
			|| page <= totalItems / pageSize
			|| modulus > 1
			|| ((modulus === 1 || modulus === 2) && !this.emptyElement && !this.allElement)) {
			this.getElements(page, pageSize, emptyElemNumber, allNumber, params);
		} else {
			this.totalItemsLoaded = false;
			this.getData(page - 1, this.gridOptions.paginationPageSize, this.startsWith)
				.subscribe(
					(previousPage: Array<T>) => {
						const itemArray: Array<T> = new Array<T>();
						const totItems: number = Number(this.getTotalItems() + emptyElemNumber + allNumber);
						if (this.emptyElement === true && this.allElement === true) {
							const lastButOneItemFromPreviousPage = previousPage[this.gridOptions.paginationPageSize - 2];
							itemArray.push(lastButOneItemFromPreviousPage);

							const lastItemFromPreviousPage = previousPage[this.gridOptions.paginationPageSize - 1];
							itemArray.push(lastItemFromPreviousPage);
						} else {
							const lastItemFromPreviousPage = previousPage[this.gridOptions.paginationPageSize - 1];
							itemArray.push(lastItemFromPreviousPage);
						}

						this.totalItemsLoaded = true;
						params.successCallback(itemArray, totItems);

					},
					() => {
						params.failCallback();
					}
				);
		}
	}

	private getElements(page: number, pageSize: number, emptyElemNumber: number, allNumber: number, params: IGetRowsParams) {
		this.totalItemsLoaded = false;
		this.getData(page, pageSize, this.startsWith)
			.subscribe(
				(v: Array<T>) => {
					const itemArray: Array<T> = new Array<T>();
					const totalItems: number = Number(this.getTotalItems() + emptyElemNumber + allNumber);

					if (this.emptyElement === true || this.allElement === true) {

						if (page === 1) {
							if (this.emptyElement === true) {
								const newElement: T = this.getInstance();
								itemArray.push(newElement);
							}

							if (this.allElement === true) {
								const allElement: T = this.getAllInstance();
								itemArray.push(allElement);
							}

							for (const originalElement of v) {
								itemArray.push(originalElement);
							}
							params.successCallback(itemArray, totalItems);
							this.totalItemsLoaded = true;

						} else {
							this.getData(page - 1, this.gridOptions.paginationPageSize, this.startsWith)
								.subscribe(
									(previousPage: Array<T>) => {

										if (this.emptyElement === true && this.allElement === true) {
											const lastButOneItemFromPreviousPage = previousPage[this.gridOptions.paginationPageSize - 2];
											itemArray.push(lastButOneItemFromPreviousPage);

											const lastItemFromPreviousPage = previousPage[this.gridOptions.paginationPageSize - 1];
											itemArray.push(lastItemFromPreviousPage);
										} else {
											const lastItemFromPreviousPage = previousPage[this.gridOptions.paginationPageSize - 1];
											itemArray.push(lastItemFromPreviousPage);
										}

										for (const originalElement of v) {
											itemArray.push(originalElement);
										}
										this.totalItemsLoaded = true;
										params.successCallback(itemArray, totalItems);
									},
									() => {
										params.failCallback();
									}
								);
						}
					} else {

						for (const originalElement of v) {
							itemArray.push(originalElement);
						}
						this.totalItemsLoaded = true;
						params.successCallback(itemArray, totalItems);
					}
				},
				error => {
					params.failCallback();
				}
			);
	}

}

import { Directive, OnInit } from '@angular/core';
import { AbstractGrid } from './abstract-grid.component';
import { Observable } from 'rxjs';
import { GridOptions, IDatasource, IGetRowsParams } from 'ag-grid-community';
import { PreferencesService } from 'systelab-preferences';
import { I18nService } from 'systelab-translate';
import { DialogService } from '../modal/dialog/dialog.service';

@Directive()
export abstract class AbstractApiGrid<T> extends AbstractGrid<T> implements IDatasource, OnInit {

	constructor(protected override preferencesService: PreferencesService, protected override i18nService: I18nService,
	            protected override dialogService: DialogService) {
		super(preferencesService, i18nService, dialogService);
	}

	protected override getInitialGridOptions(): GridOptions {
		const options = super.getInitialGridOptions();
		options.rowModelType = 'infinite';
		options.paginationPageSize = 50;
		options.cacheBlockSize = 50;
		options.cacheOverflowSize = 2;
		options.maxConcurrentDatasourceRequests = 4;
		options.maxBlocksInCache = 15;
		options.infiniteInitialRowCount = 0;
		options.datasource = this;
		return options;
	}

	public abstract getTotalItems(): number;

	protected abstract getData(page: number, itemsPerPage: number): Observable<Array<T>>;

	public getRows(params: IGetRowsParams): void {
		this.gridOptions.api.showLoadingOverlay();
		this.getData(params.endRow / this.gridOptions.paginationPageSize, this.gridOptions.paginationPageSize)
			.subscribe(
				(page: Array<T>) => this.putPage(page, this.getTotalItems(), params),
				error => this.putPage([], 0, params));
	}

	protected putPage(page: Array<T>, totalItems: number, params: IGetRowsParams): void {
		this.gridOptions.api.hideOverlay();
		params.successCallback(page, totalItems);
		if (page.length === 0) {
			this.gridOptions.api.showNoRowsOverlay();
		}
	}

	public refresh(): void {
		this.gridOptions.api.setDatasource(this);
	}
}

import { Directive, OnInit } from '@angular/core';
import { IDatasource, IGetRowsParams } from 'ag-grid-community';
import { Observable } from 'rxjs';
import { AbstractListBox } from './abstract-listbox.component';

@Directive()
export abstract class AbstractApiListBox<T> extends AbstractListBox<T> implements IDatasource, OnInit {

	public abstract override getInstance(): T;

	protected constructor() {
		super();
	}

	public override ngOnInit() {

		super.ngOnInit();

		this.gridOptions.rowModelType = 'infinite';
		this.gridOptions.paginationPageSize = 50;
		this.gridOptions.cacheBlockSize = 50;
		this.gridOptions.cacheOverflowSize = 2;
		this.gridOptions.maxConcurrentDatasourceRequests = 4;
		this.gridOptions.maxBlocksInCache = 15;
		// this.gridOptions.paginationInitialRowCount = 0;
		this.gridOptions.infiniteInitialRowCount = 0;

		this.gridOptions.datasource = this;
	}

	public abstract getTotalItems(): number;

	protected abstract getData(page: number, itemsPerPage: number): Observable<Array<T>>;

	public getRows(params: IGetRowsParams): void {

		this.gridOptions.api.showLoadingOverlay();
		const page: number = params.endRow / this.gridOptions.paginationPageSize;
		const pageSize: number = this.gridOptions.paginationPageSize;

		const showAllElementNumber: number = this.showAll ? 1 : 0;
		const totalItems: number = this.getTotalItems() + showAllElementNumber;
		const modulus: number = totalItems % pageSize;

		if (page === 1 || page <= totalItems / pageSize || modulus > 1 || (modulus === 1 && !this.showAll)) {
			this.getElements(page, pageSize, showAllElementNumber, params);
		} else {
			this.getData(page - 1, this.gridOptions.paginationPageSize)
				.subscribe(
					(previousPage: Array<T>) => {
						this.gridOptions.api.hideOverlay();
						const itemArray: Array<T> = [];
						const totItems: number = Number(this.getTotalItems() + showAllElementNumber);

						const lastItemFromPreviousPage = previousPage[this.gridOptions.paginationPageSize - 1];
						itemArray.push(lastItemFromPreviousPage);

						params.successCallback(itemArray, totItems);

					},
					() => {
						this.gridOptions.api.hideOverlay();
						params.failCallback();
					}
				);
		}
	}

	private getElements(page: number, pageSize: number, emptyElemNumber: number, params: IGetRowsParams) {
		this.getData(page, pageSize)
			.subscribe(
				(v: Array<T>) => {
					this.gridOptions.api.hideOverlay();
					const itemArray: Array<T> = [];
					const totalItems: number = Number(this.getTotalItems() + emptyElemNumber);

					if (this.showAll === true) {

						if (page === 1) {
							const newElement: T = this.getInstance();
							newElement[this.getIdField()] = this.getAllFieldID();
							newElement[this.getDescriptionField()] = this.getAllFieldDescription();
							itemArray.push(newElement);

							for (const originalElement of v) {
								itemArray.push(originalElement);
							}
							params.successCallback(itemArray, totalItems);

						} else {
							this.getData(page - 1, this.gridOptions.paginationPageSize)
								.subscribe(
									(previousPage: Array<T>) => {
										const lastItemFromPreviousPage = previousPage[this.gridOptions.paginationPageSize - 1];
										itemArray.push(lastItemFromPreviousPage);

										for (const originalElement of v) {
											itemArray.push(originalElement);
										}
										params.successCallback(itemArray, totalItems);
									},
									() => {
										params.failCallback();
									}
								);
						}
					} else {
						for (const originalElement of v) {
							itemArray.push(originalElement);
						}
						params.successCallback(itemArray, totalItems);
					}
				},
				() => {
					this.gridOptions.api.hideOverlay();
					params.failCallback();
				}
			);
	}

	public refresh() {
		this.gridOptions.api.setDatasource(this);
	}

}

import { ChangeDetectorRef, Directive, Input, OnDestroy, OnInit, Renderer2 } from '@angular/core';
import { AgRendererComponent } from 'ag-grid-angular';
import { AbstractComboBox } from '../abstract-combobox.component';
import { Observable } from 'rxjs';
import { PreferencesService } from 'systelab-preferences';

declare var jQuery: any;

export class ComboTreeNode<T> {
	public nodeData: T;
	public level: number;

	constructor(pNodeData?: T, pLevel?: number) {
		this.nodeData = pNodeData;
		this.level = pLevel;
	}
}

@Directive()
export abstract class AbstractApiTreeComboBox<T> extends AbstractComboBox<ComboTreeNode<T>> implements AgRendererComponent, OnInit, OnDestroy {

	public static readonly FAVOURITEID = 'favourite';

	@Input() public isParentSelectable = false;
	@Input() public isAllSelectable = true;

	public totalItemsLoaded = false;
	public isFirstTime = true;
	public override isTree = true;

	constructor(public override myRenderer: Renderer2, public chref: ChangeDetectorRef, public override  preferencesService?: PreferencesService) {
		super(myRenderer, chref, preferencesService);
	}

	public override ngOnInit(): void {
		this.setRowHeight();
		this.configGrid();
		this.initializeFavouriteList();
	}

	protected override configGrid() {
		this.columnDefs = [
			{
				colId:        'itemDescription',
				cellRenderer: (params: any) => {
						return this.getLabelForLevel(params.data);
				}
			}
		];

		this.gridOptions = {};

		this.gridOptions.columnDefs = this.columnDefs;

		this.gridOptions.rowHeight = AbstractComboBox.ROW_HEIGHT;
		this.gridOptions.headerHeight = 0;
		this.gridOptions.suppressCellSelection = true;
		this.gridOptions.rowSelection = 'single';
	}

	getInstance(): ComboTreeNode<T> {
		return new ComboTreeNode<T>();
	}

	getDescriptionField(): string {
		return '';
	}

	getCodeField(): string {
		return '';
	}

	getIdField(): string {
		return '';
	}

	public abstract getData(): Observable<Array<T>>;

	public abstract getTotalItems(): number;

	public abstract getLevelDescriptionField(level: number): string;

	public abstract getLevelIdField(level: number): string;

	public abstract getAllNodeId(): string | number;

	public abstract getAllNodeDescription(): string;

	public abstract getSelectionPrefix(level: number): string;

	protected getFavouriteText(): string {
		return 'Favourites';
	}

	public getLabelForLevel(comboTreeNode: ComboTreeNode<T>): string {
		if (comboTreeNode.level === 0) {
			return comboTreeNode.nodeData[this.getLevelDescriptionField(0)];
		} else if (comboTreeNode.level > 0) {
			return '<span style="padding-left: ' + (20 * comboTreeNode.level) + 'px">'
				+ comboTreeNode.nodeData[this.getLevelDescriptionField(comboTreeNode.level)]
				+ '</span>';
		}
	}

	// override
	public override closeDropDown() {
		this.isFirstTime = true;
		super.closeDropDown();
	}

	// override
	public override  loop(): void {
		let result = true;

		if (this.isDropDownOpen()) {
			// First time opened we load the table
			if (this.isFirstTime) {
				this.getRows();
			}

			if (this.totalItemsLoaded) {
				this.setDropdownHeight();
				this.setDropdownPosition();
				result = false;
			}
		}
		if (result) {
			setTimeout(() => this.loop(), 10);
		} else {
			return;
		}
	}

	// Override
	public override setDropdownHeight() {
		let totalItems = Number(this.gridOptions.api.getDisplayedRowCount());
		let calculatedHeight = 0;

		if (this.emptyElement) {
			totalItems += 1;
		}

		if (totalItems === 0) {
			calculatedHeight += 6 + AbstractComboBox.ROW_HEIGHT;
			this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'height', calculatedHeight + 'px');
		} else if (totalItems < 10) {
			calculatedHeight = 6 + AbstractComboBox.ROW_HEIGHT * totalItems;
		} else {
			calculatedHeight = AbstractComboBox.ROW_HEIGHT * 10;
		}
		this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'height', calculatedHeight + 'px');

	}

	public getRows(): void {
		this.totalItemsLoaded = false;
		this.isFirstTime = false;
		this.getData()
			.subscribe(
				(dataVector: Array<T>) => {
					const nodeVector: Array<ComboTreeNode<T>> = [];
					let previousParent: number | string;

					if (this.emptyElement) {
						const emptyElement: T = {} as T;
						emptyElement[this.getLevelIdField(0)] = '';
						emptyElement[this.getLevelDescriptionField(0)] = '';
						const emptyElementNode: ComboTreeNode<T> = new ComboTreeNode<T>(emptyElement, 0);
						nodeVector.push(emptyElementNode);
					}

					if (this.withFavourites) {
						this.initializeFavouriteList();
						if  (this.favouriteList.length > 0){
						const favouriteElement: T = {} as T;
						favouriteElement[this.getLevelIdField(0)] = AbstractApiTreeComboBox.FAVOURITEID;
						favouriteElement[this.getLevelDescriptionField(0)] = this.getFavouriteText();
						const favouriteComboNode: ComboTreeNode<T> = new ComboTreeNode<T>(favouriteElement, 0);
						nodeVector.push(favouriteComboNode);
						const favouriteElements = this.getFavouriteElements(dataVector);
						favouriteElements.forEach(currentFavouriteElement => {
							const currentFavouriteNode: ComboTreeNode<T> = new ComboTreeNode<T>(currentFavouriteElement, 1);
							nodeVector.push(currentFavouriteNode);
						});
					}}

					if (this.isAllSelectable) {
						const allElement: T = {} as T;
						allElement[this.getLevelIdField(0)] = this.getAllNodeId();
						allElement[this.getLevelDescriptionField(0)] = this.getAllNodeDescription();
						const allComboNode: ComboTreeNode<T> = new ComboTreeNode<T>(allElement, 0);
						nodeVector.push(allComboNode);
					}

					dataVector.forEach((element: T) => {
						if (!previousParent || element[this.getLevelIdField(0)] !== previousParent) {
							previousParent = element[this.getLevelIdField(0)];
							const parentComboNode: ComboTreeNode<T> = new ComboTreeNode<T>(element, 0);
							nodeVector.push(parentComboNode);
						}
						const comboNode: ComboTreeNode<T> = new ComboTreeNode<T>(element, 1);
						nodeVector.push(comboNode);
					});
					this.totalItemsLoaded = true;
					this.gridOptions.api.hideOverlay();
					this.gridOptions.api.setRowData(nodeVector);
					this.gridOptions.api.redrawRows();
				},
				() => {
					this.gridOptions.api.hideOverlay();
				}
			);
	}

	// Overrides
	protected override checkIfIsFavourite(id: string | number): void {
		const idString = id ? id.toString() : undefined;
		if (idString && idString.includes(this.getSelectionPrefix(1))) {
			super.checkIfIsFavourite(idString.substr(1));
		} else {
			super.checkIfIsFavourite(id);
		}
	}

	// Overrides
	protected override toggleFavourite(): void {
		if (this.id.toString()
			.includes(this.getSelectionPrefix(1))) {
			this.isFavourite = !this.isFavourite;
			if (this.isFavourite) {
				this.favouriteList.push(this.id.toString()
					.substr(1));
			} else {
				this.favouriteList.splice(this.favouriteList.map(String)
					.indexOf(this.id.toString()
						.substr(1)), 1);
			}
			this.preferencesService.put(this.getComboPreferencesPrefix() + '.favourites', this.favouriteList.map(String));
		} else {
			super.toggleFavourite();
		}
	}

	// Overrides
	public override onRowSelected(event: any) {
		if (event.node.selected) {
			if (this.isParentSelectable && event.node.data.nodeData[this.getLevelIdField(0)] !== AbstractApiTreeComboBox.FAVOURITEID) {
				jQuery('#' + this.comboId)
					.dropdown('toggle');
			} else if (this.isAllSelectable && event.node && event.node.data && event.node.data.level === 0) {
				if (event.node.data.nodeData[this.getLevelIdField(0)] === this.getAllNodeId()) {
					jQuery('#' + this.comboId)
						.dropdown('toggle');
				} else {
					event.node.setSelected(false);
				}
			} else if (event.node && event.node.data && event.node.data.level > 0) {
				jQuery('#' + this.comboId)
					.dropdown('toggle');
			} else {
				if (event.node) {
					event.node.setSelected(false);
				}
			}
		}
	}

	// Overrides
	public override onSelectionChanged(event: any) {
		const selectedRow = this.getSelectedRow();
		if (selectedRow !== null && selectedRow !== undefined) {
			this.id = selectedRow.nodeData[this.getLevelIdField(selectedRow.level)];
			this.description = selectedRow.nodeData[this.getLevelDescriptionField(selectedRow.level)];
			this.currentSelected = selectedRow.nodeData;
			this.level = selectedRow.level;
			if (selectedRow.level > 0
				|| (this.isAllSelectable && selectedRow.nodeData[this.getLevelIdField(0)] === this.getAllNodeId())
				|| this.isParentSelectable) {
				this.change.emit(selectedRow.nodeData);
				this.idChange.emit(this.id);
				this.closeDropDown();
			}
		}
	}

	public override refresh(params: any): boolean {
		if (this.gridOptions && this.gridOptions.api) {
			this.getRows();
			return true;
		}
	}

	private getFavouriteElements(dataVector: Array<T>): Array<T> {
		return dataVector.filter((data: T) => this.favouriteList.map(String)
			.includes(data[this.getLevelIdField(1)].toString()));
	}
}

import { AfterViewInit, Directive, ElementRef, EventEmitter, Input, OnInit, Output, ViewChild } from '@angular/core';
import { AbstractTreeListboxRendererComponent } from './renderer/abstract-tree-listbox-renderer.component';
import { StylesUtilService } from '../utilities/styles.util.service';
import { AbstractListBox } from './abstract-listbox.component';
import { Observable } from 'rxjs';

export class TreeListBoxElement<T> {
	public nodeData: T;
	public level: number;
	public selected: boolean;

	constructor(pNodeData?: T, pLevel?: number, pSelected?: boolean) {
		this.nodeData = pNodeData;
		this.level = pLevel;
		this.selected = pSelected;
	}
}

@Directive()
export abstract class AbstractApiTreeListBox<T> extends AbstractListBox<TreeListBoxElement<T>> implements OnInit, AfterViewInit {

	public columnDefs: Array<any>;
	public treeValues: Array<TreeListBoxElement<T>> = [];
	@ViewChild('hidden', {static: true}) public override hiddenElement: ElementRef;

	@Input() public updateHierarchy = true;

	public _selectedTreeItem: TreeListBoxElement<T>;

	@Input()
	set selectedTreeItem(value: TreeListBoxElement<T>) {
		this._selectedTreeItem = value;
		this.selectTreeItemInGrid();
	}

	get selectedTreeItem() {
		return this._selectedTreeItem;
	}

	@Output() selectedTreeItemChange = new EventEmitter<TreeListBoxElement<T>>();

	protected _selectedIDList: string;

	@Input()
	set selectedIDList(value: string) {
		this._selectedIDList = value;
		if (!value) {
			this.initSelectionList();
		}
		this.selectedIDListChange.emit(this._selectedIDList);
	}

	get selectedIDList() {
		this._selectedIDList = '';
		for (const selectedItem of this.multipleSelectedItemList) {
			if (this._selectedIDList && this._selectedIDList !== '') {
				this._selectedIDList += ',';
			}
			const level = selectedItem['level'] === 0 ? 0 : 1;
			this._selectedIDList += this.getSelectionPrefix(level) + selectedItem.nodeData[this.getIdField(level)];
		}
		return this._selectedIDList;
	}

	@Output() public selectedIDListChange = new EventEmitter<string>();

	public paddingSingleSelection = 0;

	protected constructor() {
		super();
	}

	protected abstract getData(): Observable<Array<T>>;

	protected abstract getSelectionPrefix(level: number): string;

	public override ngOnInit() {
		this.configGrid();
	}

	protected configGrid() {
		this.paddingSingleSelection = this.multipleSelection ? 0 : 2;
		this.gridOptions = {};
		this.gridOptions.headerHeight = 0;
		this.gridOptions.rowSelection = 'single';
		const lineHeight = StylesUtilService.getStyleValue(this.hiddenElement, 'line-height');
		if (lineHeight) {
			this.gridOptions.rowHeight = Number(lineHeight);
		} else {
			this.gridOptions.rowHeight = Number(26);
		}
		this.gridOptions.suppressCellSelection = true;

		if (this.multipleSelection) {
			this.gridOptions.suppressRowClickSelection = true;
		} else {
			this.gridOptions.suppressRowClickSelection = this.isDisabled;
		}

		this.columnDefs = [
			{
				colId:                 'id',
				cellRendererFramework: AbstractTreeListboxRendererComponent,
				cellRendererParams:    {
					changeFunction:         (e) => {
						this.changeValues(e);
					},
					level0IDField:          this.getIdField(0),
					level0DescriptionField: this.getDescriptionField(0),
					level1IDField:          this.getIdField(1),
					level1DescriptionField: this.getDescriptionField(1),
					isDisabled:             this.isDisabled,
					isMultipleSelection:    this.multipleSelection
				}
			}
		];

		this.gridOptions.getRowNodeId =
			(item) => {
				if (item.nodeData[this.getIdField(1)]) {
					return item.level + '-' + item.nodeData[this.getIdField(1)];
				} else {
					return null;
				}
			};

		this.gridOptions.columnDefs = this.columnDefs;

	}

	public ngAfterViewInit() {
		this.getRows();
	}

	protected getRows(): void {
		this.getData()
			.subscribe(
				(dataVector: Array<T>) => {
					this.loadValues(dataVector);
					this.gridOptions.api.hideOverlay();
					this.gridOptions.api.setRowData(this.treeValues);
					this.gridOptions.api.redrawRows();
					if (this.multipleSelection) {
						this.initSelectionList();
					} else if (this.selectedTreeItem) {
						this.selectTreeItemInGrid();
					}
					this.gridOptions.api.doLayout();
					this.gridOptions.api.sizeColumnsToFit();
				},
				() => {
					this.gridOptions.api.hideOverlay();
				}
			);
	}

	protected loadValues(dataVector: Array<T>) {
		this.treeValues = [];
		let previousParent: number | string;

		dataVector.forEach(element => {
			if (!previousParent || element[this.getIdField(0)] !== previousParent) {
				previousParent = element[this.getIdField(0)];
				const parentNode = new TreeListBoxElement(element, 0, false);
				this.treeValues.push(parentNode);
			}
			const node = new TreeListBoxElement(element, 1, false);
			this.treeValues.push(node);
		});
	}

	protected initSelectionList(): void {
		if (this.multipleSelection) {
			if (this._selectedIDList) {
				const selectedIDStringList: Array<string> = this._selectedIDList.split(',');
				selectedIDStringList.forEach(selectedID => {
					if (selectedID.startsWith(this.getSelectionPrefix(0))) {
						this.treeValues.forEach(element => {
							if (element.level === 0 && ((element.nodeData[this.getIdField(0)] + '') === selectedID.substring(1, selectedID.length))) {
								element.selected = true;
								this.addSelectedItem(element);
							}
						});
					}
					if (selectedID.startsWith(this.getSelectionPrefix(1))) {
						this.treeValues.forEach(element => {
							if (element.level === 1 && ((element.nodeData[this.getIdField(1)] + '') === selectedID.substring(1, selectedID.length))) {
								element.selected = true;
								this.addSelectedItem(element);
							}
						});
					}
				});
			} else {
				this.multipleSelectedItemList = [];
				this.cleanSelection();
			}
		}
	}

	// Override
	public override cleanSelection(): void {
		this.treeValues = this.treeValues.map(treeValue => {
			treeValue.selected = false;
			return treeValue;
		});
		if (this.gridOptions && this.gridOptions.api) {
			this.gridOptions.api.redrawRows();
		}
	}

	public override doClick(row: any) {
		if (!this.multipleSelection && !this.isDisabled) {
			this.selectedTreeItem = row.node.data;
			this.selectedTreeItemChange.emit(row.node.data);
		}
	}

	public changeValues(event: any) {
		if (this.multipleSelection) {
			this.addRemoveToMultipleSelectedItem(event);
			if (this.updateHierarchy) {
				if (event.level === 0) {
					this.selectUnselectChildTree(event);
				} else {
					this.selectUnselectParentTree(event);
				}
			}
			this.selectedIDListChange.emit(this.selectedIDList);
		}
		if (this.gridOptions.api) {
			this.gridOptions.api.doLayout();
			this.gridOptions.api.sizeColumnsToFit();
		}
	}

	private selectUnselectChildTree(event: any) {
		this.treeValues.forEach((value: TreeListBoxElement<T>) => {
			if (value.nodeData[this.getIdField(0)] === event.nodeData[this.getIdField(0)]) {
				value.selected = event.selected;
				this.addRemoveToMultipleSelectedItem(value);
			}
		});
	}

	private selectUnselectParentTree(event: any) {
		const parentID = event.nodeData[this.getIdField(0)];
		let allChildSelected = true;
		let anyNode = false;
		this.treeValues.forEach((value: TreeListBoxElement<T>) => {
			if (value.nodeData[this.getIdField(0)] === parentID) {
				anyNode = true;
				if (!value.selected && value.level === 1) {
					allChildSelected = false;
				}
			}
		});
		if (anyNode) {
			this.treeValues.forEach((value: TreeListBoxElement<T>) => {
				if (value.level === 0 && value.nodeData[this.getIdField(0)] === parentID) {
					value.selected = allChildSelected;
					this.addRemoveToMultipleSelectedItem(value);
				}
			});
		}
	}

	private addRemoveToMultipleSelectedItem(event: any) {
		if (this.multipleSelectedItemList && this.multipleSelectedItemList !== undefined) {
			const elementIndexInSelectedList: number = this.multipleSelectedItemList.findIndex((item) => {
				return (item.nodeData[this.getIdField(1)] === event.nodeData[this.getIdField(1)] && item['level'] === event['level']);
			});
			if (elementIndexInSelectedList < 0 && event.selected) {
				let newElement: TreeListBoxElement<T>;
				newElement = new TreeListBoxElement(event.nodeData, event['level'], event['selected']);
				this.multipleSelectedItemList.push(newElement);
			} else {
				if (elementIndexInSelectedList !== -1 && !event.selected) {
					this.multipleSelectedItemList.splice(elementIndexInSelectedList, 1);
					this.multipleSelectedItemList = this.multipleSelectedItemList.slice();
				}
			}
		} else {
			this.multipleSelectedItemList = [];
			let newElement: TreeListBoxElement<T>;
			newElement = new TreeListBoxElement(event.nodeData, event['level'], event['selected']);
			this.multipleSelectedItemList.push(newElement);
		}
	}

	public addSelectedItem(selected: TreeListBoxElement<T>) {
		if (this.containsElement(selected)) {
			this.removeElement(selected);
		} else {
			if (!this.multipleSelectedItemList) {
				this.multipleSelectedItemList = [selected];
			} else {
				this.multipleSelectedItemList.push(selected);
			}
		}
	}

	protected selectTreeItemInGrid() {
		if (this.gridOptions && this.gridOptions.api) {
			this.gridOptions.api.forEachNode(node => {
				if (!this.multipleSelection) {
					if (!this.selectedTreeItem && this.selectFirstItem) {
						if (node.rowIndex === 0) {
							node.setSelected(true);
							this.selectedTreeItem = node.data;
							this.selectedTreeItemChange.emit(node.data);
							return;
						}
					} else if (this.selectedTreeItem && this.selectedTreeItem.nodeData) {
						if (node.data.nodeData[this.getIdField(this.selectedTreeItem.level)] === this.selectedTreeItem.nodeData[this.getIdField(this.selectedTreeItem.level)] && node.data.level === this.selectedTreeItem.level) {
							node.setSelected(true);
							return;
						}
					}
				}
			});
		}
	}

	public override onModelUpdated(pEvent: any) {
	}

	public removeElement(seleccionado: TreeListBoxElement<T>) {

		for (let i = 0; i < this.multipleSelectedItemList.length; i++) {
			const element = this.multipleSelectedItemList[i];
			if (element.nodeData[this.getIdField(1)] === seleccionado.nodeData[this.getIdField(1)] && element['level'] === seleccionado['level']) {
				this.multipleSelectedItemList.splice(i, 1);
				return;
			}
		}
	}

	public containsElement(seleccionado: TreeListBoxElement<T>) {
		if (this.multipleSelectedItemList) {
			for (const element of this.multipleSelectedItemList) {
				if (element.nodeData[this.getIdField(1)] === seleccionado.nodeData[this.getIdField(1)] && element['level'] === seleccionado['level']) {
					return true;
				}
			}
		}
		return false;
	}

}

import { ChangeDetectorRef, Directive, ElementRef, EventEmitter, HostListener, Input, OnDestroy, OnInit, Output, Renderer2, ViewChild } from '@angular/core';
import { AgRendererComponent } from 'ag-grid-angular';
import { GridOptions } from 'ag-grid-community';
import { StylesUtilService } from '../utilities/styles.util.service';
import { ComboboxFavouriteRendererComponent } from './renderer/combobox-favourite-renderer.component';
import { PreferencesService } from 'systelab-preferences';

declare var jQuery: any;

@Directive()
export abstract class AbstractComboBox<T> implements AgRendererComponent, OnInit, OnDestroy {

	public static ROW_HEIGHT = -1;

	@ViewChild('input', {static: false}) public input: ElementRef;

	public comboId: string = (Math.floor(Math.random() * (999999999999 - 1))).toString();

	@Input() public customInputRenderer: any;
	@Input() public initialParams: any;

	@Input() public filter = false;
	@Input() public multipleSelection = false;
	@Input() public listSelectedValues = false;
	@Input() public allElement = false;

	@Input() public fontFamily: string;
	@Input() public fontSize: string;
	@Input() public fontWeight: string;
	@Input() public fontStyle: string;
	@Input() public withIcon: boolean;
	@Input() public withDeleteOption: boolean;
	@Input() public defaultIdValue: string | number;
	@Input() public defaultDescription: string;
	@Input() public defaultCode: string;
	@Input() public iconClass: string;
	@Input() public inputColor: string;
	@Input() public iconColor: string;

	public suppressKeyboardEvent;

	public getAllFieldIDValue(): string | number {
		return '0';
	}

	public getAllFieldDescriptionValue(): string {
		return '';
	}

	public getAllCodeFieldValue(): string {
		return '';
	}

	public getAllInstance(): T {
		const instance = this.getInstance();

		instance[this.getIdField()] = this.getAllFieldIDValue();
		instance[this.getDescriptionField()] = this.getAllFieldDescriptionValue();
		instance[this.getCodeField()] = this.getAllCodeFieldValue();

		return instance;
	}

	public _values: Array<any>;
	@Input()
	set values(newValues: Array<any>) {
		this._values = newValues;
		if (this.gridOptions) {
			this.gridOptions.rowData = this._values;
		}
		this.setCodeDescriptionById();
	}

	get values() {
		return this._values;
	}

	@Input() public isDisabled: boolean;
	@Input() public expandToParentContainerHeight = false;
	@Output() public change = new EventEmitter();
	@Output() public idChange = new EventEmitter();
	@Output() public descriptionChange = new EventEmitter();
	@Output() public levelChange = new EventEmitter();
	@Input() public allowEditInput = false;
	@Input() public emptyElement = false;
	@Input() public selectDeselectAll = false;
	@Input() public withFavourites = false;
	@Input() public preferenceName: string;

	public isFavourite = false;
	public favouriteList: Array<string | number> = [];
	public isTree = false;

	public _id: number | string;
	@Input()
	set id(value: number | string) {
		this._id = value;
		this.idChange.emit(value);
		this.setCodeDescriptionById();
		this.checkIfIsFavourite(value);
	}

	get id() {
		return this._id;
	}

	public _description: string;

	@Input()
	set description(value: string) {
		this._description = value;
		if (this.id && this.values) {
			const item: T = this.values.find(it => it[this.getIdField()] === this.id);
			if (item) {
				this._description = item[this.getDescriptionField()];
			}
		}
		this.descriptionChange.emit(this._description);
		this.fieldToShow = this._description;
	}

	get description() {
		return this._description;
	}

	public _level: number;
	@Input()
	set level(value: number) {
		this._level = value;
		this.levelChange.emit(this._level);
	}

	get level() {
		return this._level;
	}

	public _fieldToShow: string;
	@Input()
	set fieldToShow(value: string) {
		this._fieldToShow = value;
		this.fieldToShowChange.emit(this._fieldToShow);
	}

	get fieldToShow() {
		return this._fieldToShow;
	}

	@Output() public fieldToShowChange = new EventEmitter();

	public _code: string;
	@Input()
	set code(value: string) {
		this._code = value;
		if (this.id && this.values) {
			const item: T = this.values.find(it => it[this.getIdField()] === this.id);
			if (item) {
				this._code = item[this.getCodeField()];
			}
		}
		this.codeChange.emit(this._code);
	}

	get code() {
		return this._code;
	}

	@Output() public codeChange = new EventEmitter();

	public _multipleSelectedItemList: Array<T> = [];

	@Input()
	set multipleSelectedItemList(value: Array<T>) {
		this._multipleSelectedItemList = value;
		this.setDescriptionAndCodeWhenMultiple(value);
		this.multipleSelectedItemListChange.emit(this._multipleSelectedItemList);
		this.multipleSelectedIDListChange.emit(this.selectionItemListToIDList());
	}

	get multipleSelectedItemList() {
		return this._multipleSelectedItemList;
	}

	@Output() public multipleSelectedItemListChange = new EventEmitter();
	@Output() public multipleSelectedIDListChange = new EventEmitter();
	@Output() public selectedItemChange = new EventEmitter();

	@ViewChild('combobox', {static: true}) public comboboxElement: ElementRef;
	@ViewChild('dropdowntoogle', {static: false}) public dropdownToogleElement: ElementRef;
	@ViewChild('dropdownmenu', {static: false}) public dropdownMenuElement: ElementRef;
	@ViewChild('dropdown', {static: true}) public dropdownElement: ElementRef;
	@ViewChild('input', {static: false}) public inputElement: ElementRef;
	@ViewChild('hidden', {static: true}) public hiddenElement: ElementRef;

	public filterValue = '';
	public currentSelected: any = {};
	public selectionChanged = false;

	public gridOptions: GridOptions;
	public columnDefs: Array<any>;

	public params: any;

	public top = 0;
	public left = 0;
	public windowResized = false;
	public isDropdownOpened = false;
	public scrollHandler: any;

	constructor(public myRenderer: Renderer2, public chRef: ChangeDetectorRef, public preferencesService?: PreferencesService) {
	}

	public ngOnInit() {
		this.suppressKeyboardEvent = (params) => {
			const KEY_TAB = 9;
			return params.event.which === KEY_TAB;
		};
		this.setRowHeight();

		this.setStyle('font-family', this.fontFamily);
		this.setStyle('font-size', this.fontSize);
		this.setStyle('font-weight', this.fontWeight);
		this.setStyle('font-style', this.fontStyle);

		jQuery(this.comboboxElement.nativeElement)
			.on('hide.bs.dropdown', this.closeDropDown.bind(this));

		this.initializeFavouriteList();
		this.configGrid();
	}

	private setStyle(styleName: string, styleValue: string): void {
		if (styleValue) {
			this.myRenderer.setStyle(this.dropdownElement.nativeElement, styleName, styleValue);
		}
	}

	protected setDescriptionAndCodeWhenMultiple(value: Array<T>) {
		this._description = '';
		this._code = '';
		for (const selectedItem of value) {
			if (this._code !== '') {
				this._code += '; ';
			}
			this._code += selectedItem[this.getCodeField()];

			if (this._description !== '') {
				this._description += '; ';
			}
			this._description += selectedItem[this.getDescriptionField()];
		}
	}

	protected initializeFavouriteList(): void {
		const favouriteListPreference: Array<string | number> = (this.preferencesService) ? this.preferencesService.get(this.preferenceName + '.favourites') : undefined;
		if (this.withFavourites && favouriteListPreference && favouriteListPreference.length > 0) {
			this.favouriteList = favouriteListPreference;
			if (this.id != null) {
				this.checkIfIsFavourite(this.id.toString());
			}
		}
	}

	protected configGrid() {
		this.columnDefs = (this.withFavourites) ? [
			{
				colId:                 'itemDescription',
				id:                    this.getIdField(),
				field:                 this.getDescriptionField(),
				checkboxSelection:     this.multipleSelection,
				cellRendererFramework: ComboboxFavouriteRendererComponent,
				cellRendererParams:    {
					favouriteList: this.favouriteList
				}
			}
		] : [
			{
				colId:             'itemDescription',
				field:             this.getDescriptionField(),
				checkboxSelection: this.multipleSelection,
			}
		];
		this.gridOptions = {};

		this.gridOptions.columnDefs = this.columnDefs;

		this.gridOptions.rowHeight = AbstractComboBox.ROW_HEIGHT;
		this.gridOptions.headerHeight = 0;
		this.gridOptions.suppressCellSelection = true;

		if (this.multipleSelection) {
			this.gridOptions.rowSelection = 'multiple';
			this.gridOptions.suppressRowClickSelection = true;
		} else {
			this.gridOptions.rowSelection = 'single';
		}

		this.gridOptions.getRowNodeId = (item) => item[this.getIdField()] ? item[this.getIdField()] : null;

		this.configGridData();

	}

	protected configGridData() {
		this.gridOptions.rowData = this.values;
	}

	protected setRowHeight() {
		const lineHeight = StylesUtilService.getStyleValue(this.hiddenElement, 'line-height');
		AbstractComboBox.ROW_HEIGHT = Number(lineHeight ? lineHeight : 26);
	}

	public abstract getInstance(): T;

	public abstract getDescriptionField(): string;

	public abstract getCodeField(): string;

	public abstract getIdField(): string;

	public refresh(params: any): boolean {
		return true;
	}

	public agInit(params: any): void {
		this.params = params;
	}

	public getInputHeight() {
		return this.expandToParentContainerHeight ? {'height': '100%'} : undefined;
	}

	protected getComboPreferencesPrefix(): string {
		return this.preferenceName || this.constructor.name;
	}

	public doToggleFavourite(event: MouseEvent): void {
		event.stopPropagation();
		this.toggleFavourite();
	}

	public deleteValueSelected(event: MouseEvent): void {
		event.stopPropagation();
		if (this.multipleSelection) {
			if (this.multipleSelectedItemList && this.multipleSelectedItemList.length > 0) {
				this.multipleSelectedItemList = [];
			}
		} else {
			this.id = this.defaultIdValue ? this.defaultIdValue : undefined;
			this._description = this.defaultDescription ? this.defaultDescription : undefined;
			this._code = this.defaultCode ? this.defaultCode : undefined;
		}

	}

	public onComboClicked(event: any) {
		if (this.isDisabled || (this.allowEditInput && event.target.className.indexOf('input') > -1)) {
			event.stopPropagation();
		} else {
			if (!this.isDropDownOpen()) {
				this.isDropdownOpened = true;
				this.showDropDown();
			} else {
				// close
				this.checkMultipleSelectionClosed();
			}
		}
	}

	public onComboKeydown(event: any) {
		if (!this.isDropDownOpen()) {
			this.isDropdownOpened = true;
			this.showDropDown();
		} else {
			// close
			this.checkMultipleSelectionClosed();
		}
	}

	protected toggleFavourite(): void {
		this.isFavourite = !this.isFavourite;
		if (this.isFavourite) {
			this.favouriteList.push(this.id.toString());
		} else {
			this.favouriteList.splice(this.favouriteList.map(String)
				.indexOf(this.id.toString()), 1);
		}
		this.preferencesService.put(this.getComboPreferencesPrefix() + '.favourites', this.favouriteList.map(String));
	}

	public setDropdownWidth() {
		const parentWidth = this.comboboxElement.nativeElement.offsetWidth;
		this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'width', parentWidth + 'px');
		this.setGridSize();
	}

	public isDropDownOpen(): boolean {
		return this.comboboxElement.nativeElement.className.includes('show');
	}

	public closeDropDown() {
		this.isDropdownOpened = false;
		this.removeScrollHandler();
		this.resetDropDownPositionAndHeight();
		if (this.isDropDownOpen()) {
			this.myRenderer.removeClass(this.comboboxElement.nativeElement, 'show');
			this.myRenderer.removeClass(this.dropdownMenuElement.nativeElement, 'show');
		}
		this.chRef.detectChanges();
		this.checkMultipleSelectionClosed();
		this.input.nativeElement.focus();
	}

	public resetDropDownPositionAndHeight() {
		this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'top', null);
		this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'left', null);
		this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'height', '0px');
	}

	public loop(): void {
		let result = true;

		if (this.isDropDownOpen()) {
			this.setDropdownHeight();
			this.setDropdownPosition();
			this.transferFocusToGrid();
			result = false;
		}
		if (result && this.isDropdownOpened) {
			setTimeout(() => this.loop(), 10);
		} else {
			return;
		}
	}

	private transferFocusToGrid(): void {
		// scrolls to the first row
		this.gridOptions.api.ensureIndexVisible(0);

		// scrolls to the first column
		const firstCol = this.gridOptions.columnApi.getAllDisplayedColumns()[0];
		this.gridOptions.api.ensureColumnVisible(firstCol);

		// sets focus into the first grid cell
		this.gridOptions.api.setFocusedCell(0, firstCol);
	}

	public onCellKeyDown(e: any) {
		if (e.event.key === 'Enter') {
			this.gridOptions.api.selectNode(e.node);
			e.event.preventDefault();
		}
	}

	public showDropDown() {
		this.addScrollHandler();
		this.setDropdownWidth();
		if (!this.isDropDownOpen()) {
			setTimeout(() => this.loop(), 10);
		}
	}

	public clickDropDownMenu(e: Event) {
		e.stopPropagation();
	}

	public setDropdownHeight() {
		let calculatedHeight = 0;
		const totalItems: number = this.getTotalItemsForDropdownHeight();

		if (this.selectDeselectAll === true) {
			calculatedHeight += AbstractComboBox.ROW_HEIGHT + 10;
		}
		if (this.filter === true) {
			calculatedHeight += AbstractComboBox.ROW_HEIGHT + 5;
		}

		if (totalItems === 0) {
			calculatedHeight += 8 + AbstractComboBox.ROW_HEIGHT;
			this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'height', calculatedHeight + 'px');
		} else if (totalItems < 10) {
			calculatedHeight += 8 + AbstractComboBox.ROW_HEIGHT * totalItems;
			this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'height', calculatedHeight + 'px');
		} else {
			calculatedHeight += AbstractComboBox.ROW_HEIGHT * 10;
			this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'height', calculatedHeight + 'px');
		}

	}

	protected getTotalItemsInCombo(): number {
		return Number(this.values ? this.values.length : 0);
	}

	protected getTotalItemsForDropdownHeight(): number {
		let totalItems = this.getTotalItemsInCombo();
		if (this.emptyElement) {
			totalItems += 1;
		}
		if (this.allElement) {
			totalItems += 1;
		}
		return totalItems;
	}

	public setDropdownPosition() {
		this.myRenderer.setStyle(this.dropdownMenuElement.nativeElement, 'position', 'fixed');
		const dropdownParentRect: any = this.inputElement.nativeElement.getBoundingClientRect();
		this.top = dropdownParentRect.top;

		// Trick for positioning in IE11
		if (!!(<any>window).MSInputMethodContext && !!(<any>window).document.documentMode) {
			this.top = dropdownParentRect.top + this.inputElement.nativeElement.offsetHeight;
		}

		this.left = dropdownParentRect.left;
		if (this.top + this.dropdownElement.nativeElement.offsetHeight > window.innerHeight) {
			this.top = this.top - this.dropdownElement.nativeElement.offsetHeight - this.inputElement.nativeElement.offsetHeight - 2;
		}
		this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'top', this.top + 'px');
		this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'left', this.left + 'px');
	}

	public getSelectedRow(): T {
		if (this.gridOptions && this.gridOptions.api) {
			const selectedRow: Array<T> = this.gridOptions.api.getSelectedRows();
			if (selectedRow !== null) {
				return selectedRow[0];
			}
		}
		return undefined;
	}

	public doSearch(event: any) {
		this.filterValue = event.target.value;
		this.doFilter();
	}

	public doFilter() {
		const auxListArray = this.values.filter(element => element.description.toLowerCase()
			.indexOf(this.filterValue.toLowerCase()) > -1);
		this.gridOptions.api.setRowData(auxListArray);
	}

	public doSelectAll() {
		this.gridOptions.api.selectAll();
	}

	public doDeselectAll() {
		this.gridOptions.api.deselectAll();
	}

	public onSelectionChanged(event: any) {
		if (!this.multipleSelection) {
			const selectedRow = this.getSelectedRow();
			if (selectedRow !== null && selectedRow !== undefined) {
				this.id = selectedRow[this.getIdField()];
				this.code = selectedRow[this.getCodeField()];
				this.description = selectedRow[this.getDescriptionField()];
				this.currentSelected = selectedRow;
				this.change.emit(selectedRow);
				this.selectedItemChange.emit(selectedRow);
				this.closeDropDown();
				if (selectedRow[this.getIdField()]) {
					this.checkIfIsFavourite(selectedRow[this.getIdField()].toString());
				}
			}
		} else {
			this.selectionChanged = true;
		}
	}

	protected checkIfIsFavourite(id: string | number): void {
		if (id != null) {
			const idString = id.toString();
			this.isFavourite = this.favouriteList.map(String)
				.includes(idString);
		} else {
			this.isFavourite = false;
		}
	}

	public onModelUpdated() {
		this.gridOptions.api.sizeColumnsToFit();
		if (this.multipleSelection) {
			if (this.multipleSelectedItemList && this.multipleSelectedItemList.length > 0) {
				this.gridOptions.api.forEachNode(node => {
					if (this.multipleSelectedItemList.some((item) => (item !== undefined && item[this.getIdField()] === node.id))) {
						node.selectThisNode(true);
					}
				});
			}
		} else if (this._id && this._id !== undefined) {
			this.gridOptions.api.forEachNode(node => {
				if (node.id === this._id) {
					this.currentSelected = node.data;
					node.selectThisNode(true);
				}
			});
		}
	}

	public setGridSize() {
		this.gridOptions.rowHeight = AbstractComboBox.ROW_HEIGHT;
		if (this.gridOptions.api && this.columnDefs) {
			if (this.windowResized) {
				setTimeout(() => {
					this.gridOptions.api.doLayout();
					this.gridOptions.api.sizeColumnsToFit();
					this.windowResized = false;
				}, 5);
			} else {
				this.gridOptions.api.doLayout();
				this.gridOptions.api.sizeColumnsToFit();
			}
		}
	}

	// overrides
	public onRowSelected(event: any) {
		if (!this.multipleSelection) {
		} else if (event.node && event.node.data && event.node.data[this.getIdField()] !== undefined) {
			const newElement: T = this.getInstance();
			if (this.multipleSelectedItemList && this.multipleSelectedItemList !== undefined) {
				const elementIndexInSelectedList: number = this.multipleSelectedItemList.findIndex((item) => {
					return item[this.getIdField()] === event.node.data[this.getIdField()];
				});
				if (event.node.selected) {
					if (elementIndexInSelectedList < 0) {
						if (this.allElement) {
							// if the selectedNode is "all"
							if (event.node.data[this.getIdField()] === this.getAllFieldIDValue()) {
								this.multipleSelectedItemList = [];
								this.unselectAllNodesInGridpOptions();
							} else {
								// the selectedNode is NOT "all: was "all" node already selected?
								const elementAllInSelectedList: number = this.multipleSelectedItemList.findIndex((item) => {
									return item[this.getIdField()] === this.getAllFieldIDValue();
								});
								// yes, it was => unselect "all" node and empty the multipleSelectedItemList
								if (elementAllInSelectedList !== -1) {
									this.multipleSelectedItemList = [];
									this.unselectNodeAllInGridOptions();
								}
							}
						}
						// Regardless the value of allElement, we have to add the newElement using the selectedNode
						this.multipleSelectedItemList.push(event.node.data);
						this.multipleSelectedItemList = this.multipleSelectedItemList.slice();
					}
				} else {
					if (elementIndexInSelectedList !== -1) {
						this.multipleSelectedItemList.splice(elementIndexInSelectedList, 1);
						this.multipleSelectedItemList = this.multipleSelectedItemList.slice();
					}
				}
			} else {
				this.multipleSelectedItemList = [];
				this.multipleSelectedItemList.push(event.node.data);
				this.multipleSelectedItemList = this.multipleSelectedItemList.slice();
			}
			this.setDescriptionAndCodeWhenMultiple(this.multipleSelectedItemList);
		}
	}

	private unselectAllNodesInGridpOptions() {
		if (this.gridOptions && this.gridOptions.api) {
			this.gridOptions.api.forEachNode(node => {
				if (node && node.id !== this.getAllFieldIDValue()) {
					node.selectThisNode(false);
				}
			});
		}
	}

	private unselectNodeAllInGridOptions() {
		if (this.gridOptions && this.gridOptions.api) {
			this.gridOptions.api.forEachNode(node => {
				if (node && node.id === this.getAllFieldIDValue()) {
					node.selectThisNode(false);
				}
			});
		}
	}

	public setCodeDescriptionById() {
		if (this.id !== undefined && this.values) {
			const item: T = this.values.find(it => it[this.getIdField()] === this.id);
			if (item) {
				this.description = item[this.getDescriptionField()];
				this.code = item[this.getCodeField()];
			}
		}
	}

	@HostListener('window:resize', ['$event'])
	public onResize(event: any) {
		if (this.isDropDownOpen()) {
			this.closeDropDown();
		}
		const parentWidth = this.comboboxElement.nativeElement.offsetWidth;
		this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'width', parentWidth + 'px');
		this.windowResized = true;
	}

	protected isComboBoxScrolling(element: HTMLElement): boolean {
		if (element.id === this.dropdownElement.nativeElement.id) {
			return true;
		} else if (element.parentElement) {
			return this.isComboBoxScrolling(element.parentElement);
		}
		return false;
	}

	protected scroll(event) {
		if (!this.isComboBoxScrolling(event.target)) {
			this.closeDropDown();
		}
	}

	protected addScrollHandler() {
		this.scrollHandler = this.scroll.bind(this);
		window.addEventListener('scroll', this.scrollHandler, true);
	}

	protected removeScrollHandler() {
		window.removeEventListener('scroll', this.scrollHandler, true);
	}

	public ngOnDestroy() {
		this.removeScrollHandler();
		this.chRef.detach();
	}

	public removeItem(item: T) {
		const index = this.multipleSelectedItemList.findIndex(it => it[this.getIdField()] === item[this.getIdField()]);
		if (index !== -1) {
			this.multipleSelectedItemList.splice(index, 1);
			this.multipleSelectedItemList = this.multipleSelectedItemList;
		}
	}

	private selectionItemListToIDList(): Array<string | number> {
		return this.multipleSelectedItemList.map(item => item[this.getIdField()]);
	}

	public checkMultipleSelectionClosed() {
		if (this.selectionChanged) {
			this.change.emit(this.multipleSelectedItemList);
			this.selectedItemChange.emit(this.multipleSelectedItemList);
			this.multipleSelectedItemListChange.emit(this.multipleSelectedItemList);
			this.multipleSelectedIDListChange.emit(this.selectionItemListToIDList());
		}
	}

	public doGridReady(e) {
		if (this.filterValue && this.filter === true) {
			this.doFilter();
		}
	}

}

import { ChangeDetectorRef, Directive, ElementRef, EventEmitter, HostListener, Input, OnDestroy, OnInit, Output, Renderer2, ViewChild } from '@angular/core';

declare var jQuery: any;

@Directive()
export abstract class AbstractContextComponent<T> implements OnInit, OnDestroy {

	@ViewChild('dropdownparent', {static: true}) public dropdownParent: ElementRef;
	@ViewChild('dropdownmenu', {static: false}) public dropdownMenuElement: ElementRef;
	@ViewChild('dropdown', {static: false}) public dropdownElement: ElementRef;
	@ViewChild('ngcontent', {static: false}) public ngcontent: ElementRef;

	@Output() public action = new EventEmitter();

	@Input() public elementID = (Math.floor(Math.random() * (999999999999 - 1))).toString();
	@Input() public fontSize: string;
	@Input() public fontColor: string;
	@Input() public isEmbedded = false;
	@Input() public overflow = false;

	public destroyWheelListener: Function;
	public destroyMouseListener: Function;
	public destroyKeyListener: Function;
	public scrollHandler: any;
	public isOpened = false;
	protected previousActionId: string;
	protected previousShownMenu: Array<string> = [];
	protected previousMenuWidth: Array<number> = [];
	protected lastMenuLevel = 0;

	protected constructor(protected el: ElementRef, protected myRenderer: Renderer2, protected cdr: ChangeDetectorRef) {
	}

	public ngOnInit(): void {
		jQuery(this.dropdownParent.nativeElement)
			.on('hide.bs.dropdown', this.actionsAfterCloseDropDown.bind(this));
	}

	@HostListener('window:resize', ['$event'])
	public onResize(event: any): void {
		if (this.isDropDownOpened()) {
			this.closeDropDown();
		}
	}

	public isDropDownOpened(): boolean {
		return this.dropdownParent.nativeElement.className.includes('show');
	}

	protected loop(x: number, y: number): void {
		if (this.isDropDownOpened()) {
			this.myRenderer.setStyle(this.dropdownMenuElement.nativeElement, 'position', 'fixed');
			if (this.isEmbedded) {
				this.myRenderer.setStyle(this.dropdownMenuElement.nativeElement, 'transform', 'unset');
			}
			this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'position', 'absolute');
			y = y - this.dropdownParent.nativeElement.offsetHeight;
			if (y + this.dropdownElement.nativeElement.offsetHeight > window.innerHeight) {
				y = y - this.dropdownElement.nativeElement.offsetHeight;
			}
			if (x + this.dropdownElement.nativeElement.offsetWidth > window.innerWidth) {
				x = x - this.dropdownElement.nativeElement.offsetWidth;
			}
			this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'top', y + 'px');
			this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'left', x + 'px');
			this.myRenderer.setStyle(this.dropdownMenuElement.nativeElement, 'visibility', 'visible');
			this.addListeners();

		} else {
			setTimeout(() => this.loop(x, y), 10);
		}
	}

	public showDropDown(x: number, y: number): void {
		setTimeout(() => this.loop(x, y), 10);
	}

	public resetDropDownPositionAndHeight(): void {
		this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'top', null);
		this.myRenderer.setStyle(this.dropdownElement.nativeElement, 'left', null);
	}

	protected getFirstChildLeft(selectedChild: ElementRef): number {
		let firstChildLeft = this.dropdownElement.nativeElement.offsetWidth + 15;
		const firstChildAbsoluteLeft = this.dropdownElement.nativeElement.offsetLeft;

		if (firstChildAbsoluteLeft + this.dropdownElement.nativeElement.offsetWidth +
			selectedChild.nativeElement.offsetWidth > window.innerWidth) {
			firstChildLeft = -selectedChild.nativeElement.offsetWidth + 10;
		}
		return firstChildLeft;
	}

	protected getFirstChildLeftWithLevels(selectedChild: ElementRef, optionLevel: number, previousMenuWidth: Array<number>): number {
		let firstChildLeft;
		let accumulativeLeft = 0;
		const firstChildAbsoluteLeft = this.dropdownElement.nativeElement.offsetLeft;

		if (optionLevel < 1) {
			firstChildLeft = this.dropdownElement.nativeElement.offsetWidth + 12;
		} else {
			firstChildLeft = previousMenuWidth[optionLevel - 1] + 12;
			for (let i = 0; i < optionLevel; i++) {
				accumulativeLeft = accumulativeLeft + previousMenuWidth[i];
			}
		}

		if (firstChildAbsoluteLeft + this.dropdownElement.nativeElement.offsetWidth + accumulativeLeft +
			selectedChild.nativeElement.offsetWidth > window.innerWidth) {
			firstChildLeft = -selectedChild.nativeElement.offsetWidth + 15;
		}
		return firstChildLeft;
	}

	protected getFirstChildTop(event: any, selectedChild: ElementRef): number {
		const firstChildAbsoluteTop = event.clientY;
		let firstChildRelativeTop = event.target.offsetTop;

		if (firstChildAbsoluteTop + selectedChild.nativeElement.offsetHeight > window.innerHeight) {
			firstChildRelativeTop = firstChildRelativeTop - selectedChild.nativeElement.offsetHeight;
		}
		return firstChildRelativeTop;
	}

	public actionsAfterCloseDropDown(): void {
		this.previousShownMenu = [];
		this.previousMenuWidth = [];
		this.lastMenuLevel = 0;
		this.previousActionId = undefined;
		this.isOpened = false;
		this.cdr.detectChanges();
		this.removeScrollHandler();
		if (this.destroyWheelListener) {
			this.destroyWheelListener();
		}
		if (this.destroyKeyListener) {
			this.destroyKeyListener();
		}
		if (this.destroyMouseListener) {
			this.destroyMouseListener();
		}
		this.resetDropDownPositionAndHeight();
	}

	public closeDropDown(): void {
		if (this.isDropDownOpened()) {
			this.myRenderer.removeAttribute(this.dropdownParent.nativeElement, 'aria-expanded');
			this.myRenderer.removeClass(this.dropdownParent.nativeElement, 'show');
			this.myRenderer.removeClass(this.dropdownMenuElement.nativeElement, 'show');
			this.myRenderer.setStyle(this.dropdownMenuElement.nativeElement, 'visibility', 'hidden');
		}
		this.actionsAfterCloseDropDown();
	}

	protected addListeners(): void {
		this.addScrollHandler();
		this.destroyMouseListener = this.myRenderer.listen('window', 'click', (evt: MouseEvent) => {
			this.handleMouseEvents(evt);
		});
		this.destroyWheelListener = this.myRenderer.listen('window', 'scroll', (evt: WheelEvent) => {
			this.handleWheelEvents(evt);
		});
		this.destroyKeyListener = this.myRenderer.listen('document', 'keydown', (evt: KeyboardEvent) => {
			this.handleKeyboardEvents(evt);
		});
	}

	protected handleKeyboardEvents(event: KeyboardEvent): void {
		if (event.key === 'Escape') {
			if (this.isDropDownOpened()) {
				this.closeDropDown();
			}
		}
	}

	protected handleWheelEvents(event: WheelEvent): void {
		this.checkTargetAndClose(event.target);
	}

	protected handleMouseEvents(event: MouseEvent): void {
		this.checkTargetAndClose(event.target);
	}

	protected scroll(event: any): void {
		this.checkTargetAndClose(event.target);
	}

	protected checkTargetAndClose(target: any): void {
		if (!this.checkIfNgContent(target)) {
			if (this.isDropDownOpened()) {
				this.closeDropDown();
			}
		}
	}

	public ngContentStopPropagation(event: any): void {
		event.stopPropagation();
	}

	protected checkIfNgContent(target: any): boolean {
		let currentElement = target;
		while (currentElement !== this.dropdownElement && currentElement) {
			if (currentElement === this.ngcontent.nativeElement) {
				return true;
			} else {
				currentElement = currentElement.parentElement;
			}
		}
		return false;
	}

	protected addScrollHandler(): void {
		this.scrollHandler = this.scroll.bind(this);
		window.addEventListener('scroll', this.scrollHandler, true);
	}

	protected removeScrollHandler(): void {
		window.removeEventListener('scroll', this.scrollHandler, true);
	}

	public ngOnDestroy(): void {
		this.removeScrollHandler();
	}

	public dotsClicked(event: MouseEvent): void {
		if (!this.isDropDownOpened()) {
			this.elementID = (Math.floor(Math.random() * (999999999999 - 1))).toString();
			this.hideDivUntilIsPositioned(event.clientX, event.clientY);
		}
	}

	protected hideDivUntilIsPositioned(x: number, y: number): void {
		// hide the div until is positioned in event x y position to avoid flick
		this.myRenderer.setStyle(this.dropdownMenuElement.nativeElement, 'visibility', 'hidden');
		this.isOpened = true;
		this.cdr.detectChanges();
		this.showDropDown(x, y);
	}

	public open(event: MouseEvent): void {
		jQuery('#' + this.elementID)
			.dropdown('toggle');
		if (!this.isDropDownOpened()) {
			// Add class manually because is not set when jquery.dropdwon toogle is executed
			this.myRenderer.addClass(this.dropdownParent.nativeElement, 'show');
			this.hideDivUntilIsPositioned(event.clientX, event.clientY);
		}
	}

	public toggle(elementID: string): void {
		jQuery('#' + elementID)
			.toggle();
	}
}

import { Directive, ElementRef, EventEmitter, Input, Output, QueryList, ViewChild, ViewChildren, OnInit } from '@angular/core';
import { AbstractContextComponent } from './abstract-context.component';
import { ContextMenuOption } from './context-menu-option';

@Directive()
export abstract class AbstractContextMenuComponent<T> extends AbstractContextComponent<T> implements OnInit {

	@ViewChildren('childdropdownmenu') public childDropdownMenuElement: QueryList<ElementRef>;
	@ViewChild('scrollableList', {static: false}) public scrollableList: ElementRef;

	@Output() public override action = new EventEmitter();

	public readonly levelSeparator = '_|_';
	public hasIcons = false;
	private contextMenuOptionsList: Array<T>;

	@Input()
	set contextMenuOptions(value: Array<T>) {
		this.contextMenuOptionsList = value;
		this.checkIfHasIcons();
	}

	get contextMenuOptions(): Array<T> {
		return this.contextMenuOptionsList;
	}

	public override ngOnInit(): void {
		super.ngOnInit();
		this.checkIfHasIcons();
	}

	public override dotsClicked(event: MouseEvent): void {
		if (this.existsAtLeastOneActionEnabled()) {
			super.dotsClicked(event);
		} else {
			event.stopPropagation();
		}
	}

	public override open(event: MouseEvent): void {
		if (this.existsAtLeastOneActionEnabled()) {
			super.open(event);
		} else {
			event.stopPropagation();
		}
	}

	public doClick(event: any, elementID: string, action: ContextMenuOption, parent?: ContextMenuOption): void {
		if (this.isEnabled(elementID, action.actionId)) {
			this.executeAction(event, elementID, action.actionId);
		}
	}

	public doClickWithAction(event: any, elementID: string, actionId: string): void {
		if (this.isEnabled(elementID, actionId)) {
			this.executeAction(event, elementID, actionId);
		}
	}

	public doMouseOver(event: any, elementID: string, actionId: string): void {
		if (this.isEnabled(elementID, actionId)) {
			const optionAcitionId = this.getOptionDetailsActionId(actionId);

			const selectedChild = this.childDropdownMenuElement.toArray()
				.find((elem) => elem.nativeElement.id === (optionAcitionId + this.elementID));

			this.showSubmenu(event, actionId, selectedChild, this.elementID);
		}
	}

	public getSelfReference(): AbstractContextMenuComponent<T> {
		return this;
	}

	public getMenuLevel(actionId: string): number {
		const actions: string[] = actionId ? actionId.split(this.levelSeparator) : [];
		return actions.length - 1;
	}

	public getOptionDetailsActionId(actionId: string): string {
		const option = this.getOption(actionId);
		return option ? option.actionId : undefined;
	}

	public getOptionDetailsHasChildren(actionId: string): boolean {
		const option = this.getOption(actionId);
		return option ? option.hasChildren() : false;
	}

	public showSubmenu(event: any, actionId: string, selectedChild: ElementRef, elementId: string): void {
		const optionActionId = this.getOptionDetailsActionId(actionId);
		const optionHasChildren = this.getOptionDetailsHasChildren(actionId);
		const optionLevel = this.getMenuLevel(actionId);

		if (optionHasChildren) {
			event.stopPropagation();
			event.preventDefault();

			if (this.previousActionId !== optionActionId) {
				this.previousActionId = optionActionId;
				this.hideSubmenus(optionLevel);
				this.lastMenuLevel = optionLevel + 1;

				this.previousShownMenu.push(optionActionId + elementId);

				this.toggle(optionActionId + elementId);

				this.previousMenuWidth[this.lastMenuLevel - 1] = selectedChild.nativeElement.offsetWidth;

				const leftPosition = this.getFirstChildLeftWithLevels(selectedChild, optionLevel, this.previousMenuWidth);

				this.myRenderer.setStyle(selectedChild.nativeElement, 'top', this.getFirstChildTop(event, selectedChild) + 'px');
				this.myRenderer.setStyle(selectedChild.nativeElement, 'left', leftPosition + 'px');
			}
		} else {
			this.hideSubmenus(optionLevel);
			this.lastMenuLevel = optionLevel;

			event.stopPropagation();
			event.preventDefault();
			this.previousActionId = optionActionId;

		}
	}

	protected checkIfHasIcons(): void {
		this.hasIcons = false;
	}

	protected override checkTargetAndClose(target: any): void {
		if (!this.checkIfNgContent(target)) {
			if (target !== this.scrollableList.nativeElement && this.isDropDownOpened()) {
				if (this.childDropdownMenuElement) {
					if (!this.childDropdownMenuElement.toArray()
						.some((elem) => target === elem.nativeElement)) {
						this.closeDropDown();
					}
				} else {
					this.closeDropDown();
				}
			}
		}
	}

	protected hideSubmenus(untilLevel: number): void {
		if (untilLevel < this.lastMenuLevel) {
			for (let i = this.lastMenuLevel; i > untilLevel; i--) {
				if (this.previousShownMenu[i - 1]) {
					this.toggle(this.previousShownMenu[i - 1]);
				}
				this.previousShownMenu.pop();
				this.lastMenuLevel = i - 1;
			}
		}
	}

	public abstract openWithOptions(event: MouseEvent, newContextMenuOptions: Array<T>): void;

	public abstract isEnabled(elementId: string, actionId: string): boolean;

	public abstract isIconEnabled(elementId: string, actionId: string): boolean;

	public abstract executeAction(event: any, elementId: string, actionId: string, parentAction?: string): void;

	protected abstract existsAtLeastOneActionEnabled(): boolean;

	protected abstract getOption(actionId: string);
}

import { Directive, ElementRef, EventEmitter, Input, OnInit, Output, ViewChild } from '@angular/core';
import { ColDef, Column, ColumnApi, GridOptions, RowNode } from 'ag-grid-community';
import { GridContextMenuOption } from './contextmenu/grid-context-menu-option';
import { GridContextMenuActionData } from './contextmenu/grid-context-menu-action-data';
import { DialogService } from '../modal/dialog/dialog.service';
import { PreferencesService } from 'systelab-preferences';
import { I18nService } from 'systelab-translate';
import { StylesUtilService } from '../utilities/styles.util.service';
import { GridContextMenuComponent, GridRowMenuActionHandler } from './contextmenu/grid-context-menu-component';
import { timer } from 'rxjs';
import { GridColumnsOptions } from './options/grid-column-options';
import { GridColumnOptionsDialog, GridColumnOptionsDialogParameters } from './options/grid-column-options-dialog.component';
import { GridContextMenuCellRendererComponent } from './contextmenu/grid-context-menu-cell-renderer.component';
import { TwoListItem } from '../twolist/two-list-utilities';
import { GridHeaderContextMenu, GridHeaderMenuActionHandler } from './contextmenu/grid-header-context-menu.component';

export type rowSelectionType = 'single' | 'multiple';

@Directive()
export abstract class AbstractGrid<T> implements OnInit, GridRowMenuActionHandler, GridHeaderMenuActionHandler {

	public static readonly contextMenuColId = 'contextMenu';
	public static readonly selectionColId = 'selectCol';

	public gridOptions: GridOptions;
	public overlayNoRowsTemplate;
	public overlayLoadingTemplate;

	@Input() public headerMenu: Array<GridContextMenuOption<Object>>;
	@Input() public menu: Array<GridContextMenuOption<T>>;

	@Input() public preferenceName: string;
	@Input() public multipleSelection = false;
	@Input() public showChecks = false;
	@Input() public rowData: Array<T> = [];
	@Input() public noRowsText;
	@Input() public loadingText;

	@Output() public action = new EventEmitter();
	@Output() public clickRow = new EventEmitter();
	@Output() public rowDragEnd = new EventEmitter();
	@Output() public viewportChanged = new EventEmitter();

	@ViewChild('hidden', {static: true}) public hiddenElement: ElementRef;
	@ViewChild('popupmenu', {static: false}) public popupmenu: GridContextMenuComponent<T>;
	@ViewChild('headerpopupmenu', {static: false}) public headerPopupMenu: GridHeaderContextMenu<Object>;

	protected firstSizeToFitExecuted = false;

	constructor(protected preferencesService: PreferencesService, protected i18nService: I18nService,
				protected dialogService: DialogService) {
	}

	public ngOnInit(): void {

		this.gridOptions = this.getInitialGridOptions();

		if (this.noRowsText) {
			this.overlayNoRowsTemplate = this.noRowsText;
			this.overlayLoadingTemplate = this.loadingText;
		}
	}

	protected getInitialGridOptions(): GridOptions {
		const rowHeight = StylesUtilService.getStyleValue(this.hiddenElement, 'line-height');
		const headerHeight = StylesUtilService.getStyleValue(this.hiddenElement, 'height');

		const options: GridOptions = {};

		options.columnDefs = this.getColumnDefsWithOptions();
		options.rowHeight = Number(rowHeight);
		options.headerHeight = Number(headerHeight);
		options.suppressDragLeaveHidesColumns = true;
		options.suppressCellSelection = true;
		options.enableRangeSelection = true;
		options.stopEditingWhenGridLosesFocus = true;
		options.singleClickEdit = true;
		options.defaultColDef = {
			resizable: this.isColResizeEnabled()
		};
		options.rowSelection = this.getRowSelectionType();
		options.localeText = {
			noRowsToShow: this.i18nService.instant('COMMON_NO_ROWS_TO_SHOW'),
			loadingOoo:   this.i18nService.instant('COMMON_LOADING')
		};

		if (this.hideHeader()) {
			options.headerHeight = 0;
		}
		if (this.showChecks) {
			if (this.multipleSelection) {
				options.suppressRowClickSelection = true;
			}
		}
		options.isFullWidthCell = (rowNode: RowNode) => this.getIsFullWidthCell(rowNode);
		options.fullWidthCellRendererFramework = this.getFullWidthCellRenderer();
		options.context = {componentParent: this};

		return options;
	}

	public onModelUpdated(event: any) {
		this.gridOptions.api.sizeColumnsToFit();
		return event;
	}

	public doGridReady(event: any): void {
		this.loadColumnsStateFromPreferences();
		this.firstSizeToFitExecuted = true;
		this.gridOptions.api.sizeColumnsToFit();

		this.gridOptions.api.doLayout();
		this.gridOptions.api.addEventListener('columnMoved', this.saveColumnsStateInPreferences.bind(this));
	}

	protected saveColumnsStateInPreferences(): void {
		if (this.firstSizeToFitExecuted) {
			this.preferencesService.put(this.getGridOptionsPreferencesPrefix(), this.gridOptions.columnApi.getColumnState());
		}
	}

	protected loadColumnsStateFromPreferences(): void {
		this.loadColumnsState(this.getGridOptionsPreferencesPrefix(), this.gridOptions.columnApi);
	}

	private loadColumnsState(prefix: string, columnApi: ColumnApi): void {

		if (this.preferencesService.get(prefix)) {

			const gridOptionsPreferences: Array<any> = this.preferencesService.get(prefix);

			// Filtered preferences columns that are not in the current columnDef.
			const filteredGridOptionsPreferences: Array<any> = gridOptionsPreferences
				.filter(colPref => columnApi.getAllColumns()
					.some(column => colPref.colId === column.getColId()));

			// Show new added columns
			columnApi.getAllColumns()
				.filter(column => !filteredGridOptionsPreferences.some(colPref => colPref.colId === column.getColId()))
				.forEach(column => {
					const newColumn: any = {
						'colId':         column.getColId(),
						'hide':          !column.isVisible(),
						'aggFunc':       null,
						'width':         column.getActualWidth(),
						'pivotIndex':    null,
						'pinned':        null,
						'rowGroupIndex': null
					};

					if (column.getColId() === AbstractGrid.contextMenuColId || column.getColId() === AbstractGrid.selectionColId) {
						filteredGridOptionsPreferences.unshift(newColumn);
					} else {
						filteredGridOptionsPreferences.push(newColumn);
					}
				});

			this.setColumnWidthToFitContent(columnApi, filteredGridOptionsPreferences);

			// Override pinned property saved in preferences
			const pinnedCol = filteredGridOptionsPreferences.find(column => column.colId === AbstractGrid.contextMenuColId || column.colId === AbstractGrid.selectionColId);
			if (pinnedCol) {
				pinnedCol['pinned'] = 'left';
			}

			columnApi.setColumnState(filteredGridOptionsPreferences);
		}
	}

	private setColumnWidthToFitContent(columnApi: ColumnApi, filteredGridOptionsPreferences: Array<any>) {
		// Set to null width of preferences of columns without supressSizeToFit
		// If not set to null these columns are not sizedtofit
		columnApi.getAllColumns()
			.filter(column => !column.getColDef().suppressSizeToFit)
			.forEach(column => {
				const columnPref: any = filteredGridOptionsPreferences.find(colPref => colPref.colId === column.getColId());
				columnPref.width = null;
			});
	}

	private getContextMenuColumnDef(width: number) {
		return {
			colId:                 AbstractGrid.contextMenuColId,
			headerName:            '',
			pinned:                'left',
			width:                 width,
			suppressSizeToFit:     true,
			resizable:             false,
			suppressMovable:       true,
			cellRendererFramework: GridContextMenuCellRendererComponent
		};
	}

	private getCheckColumnDef(width: number) {
		return {
			colId:             AbstractGrid.selectionColId,
			headerName:        '',
			checkboxSelection: true,
			pinned:            'left',
			width:             width,
			suppressSizeToFit: true,
			resizable:         false,
			suppressMovable:   true
		};
	}

	protected abstract getColumnDefs(): Array<any>;

	protected getColumnDefsWithOptions(): Array<any> {

		const colDefs: Array<any> = this.getColumnDefs();

		if (this.menu && this.menu.length > 0) {
			colDefs.unshift(this.getContextMenuColumnDef(this.getContextMenuColumnWidth()));
		}
		if (this.showChecks) {
			colDefs.unshift(this.getCheckColumnDef(this.getCheckColumnWidth()));
		}
		colDefs.forEach((colDef: ColDef) => this.suppressColumnSizeToFit(colDef));

		return colDefs;
	}

	protected hideHeader(): boolean {
		return false;
	}

	protected getIsFullWidthCell(rowNode: RowNode): boolean {
		return false;
	}

	public getFullWidthCellRenderer(): any {
		return undefined;
	}

	protected getGridOptionsPreferencesPrefix(): string {
		return this.preferenceName || this.constructor.name;
	}

	public executeContextMenuAction(elementId: string, actionId: string): void {
		const option: GridContextMenuOption<T> = this.menu.find(opt => opt.actionId === actionId);
		const rowId = Number(elementId.substr(elementId.indexOf('row'))
			.replace('row', ''));
		const data: T = this.gridOptions.api.getModel()
			.getRow(rowId).data;
		const rowsSelected: Array<T> = this.gridOptions.api.getSelectedRows();

		const actionData: GridContextMenuActionData<T> = new GridContextMenuActionData(rowId.toString(), actionId, data, this.gridOptions, rowsSelected);
		if (option && option.action && data !== undefined) {
			option.action(actionData);
		} else {
			this.action.emit(actionData);
		}
		this.popupmenu.closeDropDown();
	}

	public isContextMenuOptionEnabled(elementId: string, actionId: string): boolean {

		const option: GridContextMenuOption<T> = this.menu.find(opt => opt.actionId === actionId);
		const rowId = Number(elementId.substr(elementId.indexOf('row'))
			.replace('row', ''));
		const data: T = this.gridOptions.api.getModel()
			.getRow(rowId).data;

		if (option && option.isActionEnabled && data !== undefined) {
			return option.isActionEnabled(data);
		}
		return true;
	}

	public executeHeaderContextMenuAction(elementId: string, actionId: string, headerData: any): void {

		const option: GridContextMenuOption<Object> = this.headerMenu.find(opt => opt.actionId === actionId);

		if (option && option.action) {
			const actionData: GridContextMenuActionData<Object> = new GridContextMenuActionData(elementId, actionId, headerData, this.gridOptions);
			return option.action(actionData);
		} else {
			const actionData: GridContextMenuActionData<Object> = new GridContextMenuActionData(elementId, actionId, headerData, this.gridOptions);
			this.action.emit(actionData);
		}
		this.headerPopupMenu.closeDropDown();
	}

	public isHeaderContextMenuOptionEnabled(elementId: string, actionId: string, headerData: any): boolean {

		const option: GridContextMenuOption<Object> = this.headerMenu.find(opt => opt.actionId === actionId);
		if (option && option.isActionEnabled) {
			return option.isActionEnabled(headerData);
		}
		return true;
	}

	protected isColResizeEnabled(): boolean {
		return true;
	}

	private suppressColumnSizeToFit(colDef: ColDef): void {
		if (colDef.width) {
			colDef.suppressSizeToFit = true;
		}
	}

	public onRowSelected(event: any) {
	}

	protected getRowSelectionType(): rowSelectionType {
		return this.multipleSelection ? 'multiple' : 'single';
	}

	public getSelectedRows(): Array<T> {
		return this.gridOptions.api.getSelectedRows();
	}

	public getSelectedRow(): T {
		const selected = this.getSelectedRows();
		return selected.length > 0 ? selected[0] : null;
	}

	public selectRow(index: number): void {
		this.gridOptions.api.ensureIndexVisible(index);
		timer(200)
			.subscribe(() => this.gridOptions.api.selectIndex(index, false, false));
	}

	public doClick(event: any): void {
		if (event.column.colId === 'contextMenu') {
			event.node.setSelected(true);
		} else {
			if (event.column.colId === 'selectCol') {
				event.node.setSelected(!event.node.isSelected());
			} else {
				if (!event.column.isCellEditable(event.node)) {
					const value = (event.event.ctrlKey && this.multipleSelection && !this.showChecks) ? event.event : event.data;
					this.clickRow.emit(value);
				}
			}
		}
	}

	public doColumnResized(event: any): void {
		this.saveColumnsStateInPreferences();
	}

	public doViewportChanged(): void {
		this.viewportChanged.emit();
	}

	public doGridSizeChanged(event: any): void {
		if (this.gridOptions.api) {
			this.gridOptions.api.sizeColumnsToFit();
		}
	}

	public showOptions(): void {
		const parameters: GridColumnOptionsDialogParameters = GridColumnOptionsDialog.getParameters();

		parameters.columnOptions = this.getGridColumnOptions(this.gridOptions.columnApi, this.getColumnDefs());

		this.dialogService.showDialog(GridColumnOptionsDialog, parameters)
			.subscribe(
				(columnsOptions: GridColumnsOptions) => {
					if (columnsOptions) {
						this.applyGridColumnOptions(this.gridOptions.columnApi, columnsOptions);
						this.gridOptions.api.sizeColumnsToFit();
						this.saveColumnsStateInPreferences();
					}
				}
			);
	}

	protected getGridColumnOptions(columnApi: ColumnApi, columnDefs: Array<any>): GridColumnsOptions {
		const options = new GridColumnsOptions();

		options.available = columnApi.getAllColumns()
			.filter(column => !columnApi.getColumn(column.getColDef().colId)
				.isVisible())
			.map(column => new TwoListItem(column.getColDef().headerName, column.getColDef().colId, false, false));

		options.initialAvailableColumns = columnApi.getAllColumns()
			.map(column => new TwoListItem(column.getColDef().headerName, column.getColDef().colId, false, false));

		options.visible = columnApi.getAllDisplayedColumns()
			.filter(column => column.getColId() !== 'contextMenu')
			.map(column => new TwoListItem(column.getColDef().headerName, column.getColDef().colId, false, true));

		options.defaultVisibleColumns = columnDefs.filter(column => !column.hide)
			.map(column => new TwoListItem(column.headerName, column.colId, false, true));

		options.defaultHiddenColumns = columnDefs.filter(column => column.hide)
			.map(column => new TwoListItem(column.headerName, column.colId, false, false));
		return options;
	}

	protected applyGridColumnOptions(columnApi: ColumnApi, columnOptions: GridColumnsOptions): void {
		const numberOfFixedInitialColumns = (columnApi.getColumn('contextMenu') !== null) ? 1 : 0;

		columnOptions.visible.forEach((tlp, index) => {
			const col: Column = columnApi.getAllColumns()
				.find((column: Column) => column.getColDef().colId === tlp.colId);
			col.setVisible(true);
			columnApi.moveColumn(col.getColId(), index + numberOfFixedInitialColumns);
		});

		columnApi.getAllColumns()
			.forEach((column) => {
				if (column.getColId() !== 'contextMenu') {
					if (!columnOptions.visible.some(tlp => tlp.colId === column.getColDef().colId)) {
						columnApi.setColumnVisible(column.getColId(), false);
					}
				}
			});
	}

	public dotsClicked(rowIndex: number, data: T | Array<T>, event: MouseEvent): void {
		this.popupmenu.setActionManager(this);
		this.popupmenu.setRowIndex(rowIndex);
		if (this.existsAtLeastOneActionEnabled(data)) {
			timer(200)
				.subscribe(() => this.popupmenu.openWithOptions(event, this.menu));
		} else {
			event.stopPropagation();
		}
	}

	public headerDotsClicked(headerData: Object, event: MouseEvent): void {
		this.headerPopupMenu.setActionManager(this);
		this.headerPopupMenu.setHeaderData(headerData);
		if (this.existsAtLeastOneHeaderActionEnabled(headerData)) {
			timer(200)
				.subscribe(() => this.headerPopupMenu.openWithOptions(event, this.headerMenu,));
		} else {
			event.stopPropagation();
		}
	}

	protected existsAtLeastOneHeaderActionEnabled(data: Object | Array<Object>): boolean {
		return this.headerMenu ? this.headerMenu.some(menuOption => this.isMenuOptionEnabled(menuOption, data)) : false;
	}

	protected existsAtLeastOneActionEnabled(data: T | Array<T> | Object | Array<Object>): boolean {
		if (this.menu) {
			return this.menu.some(menuOption => this.isMenuOptionEnabled(menuOption, data));
		} else {
			return false;
		}
	}

	private isMenuOptionEnabled(menuOption: GridContextMenuOption<T>, data: T | Array<T> | Object | Array<Object>): boolean {
		if (menuOption.isActionEnabled) {
			return menuOption.isActionEnabled.apply(null, [data]);
		} else {
			return true;
		}
	}

	public onRowDragEnd(event: any): void {
		this.rowDragEnd.emit(event);
	}

	protected getContextMenuColumnWidth(): number {
		return 40;
	}

	protected getCheckColumnWidth(): number {
		return 35;
	}
}

import { Directive, ElementRef, EventEmitter, Input, OnInit, Output, ViewChild } from '@angular/core';
import { StylesUtilService } from '../utilities/styles.util.service';
import { ColDef, GridOptions } from 'ag-grid-community';

@Directive()
export abstract class AbstractListBox<T> implements OnInit {

	public gridOptions: GridOptions;
	@ViewChild('hidden', {static: true}) public hiddenElement: ElementRef;
	public _values: Array<T>;
	@Input()
	set values(newValues: Array<T>) {
		this._values = newValues;
		if (this.gridOptions && this.gridOptions.api) {
			this.gridOptions.api.setRowData(this._values);
		}
	}

	get values() {
		return this._values;
	}

	@Input() public rowDrag = false;
	@Input() public isDisabled: boolean;

	public _selectedItem: T;

	@Input()
	set selectedItem(value: T) {
		this._selectedItem = value;
		this.selectItemInGrid();
	}

	get selectedItem() {
		return this._selectedItem;
	}

	@Output() selectedItemChange = new EventEmitter<T>();
	@Output() public multipleSelectedIDListChange = new EventEmitter();
	@Output() public rowDragEnd = new EventEmitter();

	@Input() public selectFirstItem = false;
	@Input() public multipleSelection = false;
	@Input() public showAll = false;
	@Input() public hideChecks = false;

	protected _multipleSelectedItemList: Array<T>;

	@Input()
	set multipleSelectedItemList(value: Array<T>) {
		this._multipleSelectedItemList = value;
		this.selectItemInGrid();
		this.multipleSelectedItemListChange.emit(this._multipleSelectedItemList);
		this.multipleSelectedIDListChange.emit(this.selectionItemListToIDList());
	}

	get multipleSelectedItemList(): Array<T> {
		return this._multipleSelectedItemList;
	}

	@Output() public multipleSelectedItemListChange = new EventEmitter();

	protected constructor() {
	}

	protected abstract getIdField(level?: number): string;

	protected abstract getDescriptionField(level?: number): string;

	public abstract getInstance(): T;

	public ngOnInit() {

		const rowHeight = StylesUtilService.getStyleValue(this.hiddenElement, 'line-height');

		this.gridOptions = {};

		this.gridOptions.columnDefs = this.getColumnDefsWithOptions();

		if (this.multipleSelection && !this.hideChecks) {
			this.gridOptions.suppressRowClickSelection = true;
			this.gridOptions.rowClassRules = {
				'ag-row-disabled': (params) => {
					return this.isDisabled;
				},
			};
		} else {
			this.gridOptions.suppressRowClickSelection = this.isDisabled;
		}

		this.gridOptions.rowHeight = Number(rowHeight);
		this.gridOptions.suppressDragLeaveHidesColumns = true;
		this.gridOptions.suppressCellSelection = true;
		this.gridOptions.enableRangeSelection = !this.isDisabled;
		this.gridOptions.defaultColDef = {};
		this.gridOptions.defaultColDef.resizable = false;
		this.gridOptions.rowSelection = this.multipleSelection ? 'multiple' : 'single';
		this.gridOptions.suppressRowDeselection = this.isDisabled;

		this.gridOptions.context = {componentParent: this};

		this.gridOptions.headerHeight = 0;
		this.gridOptions.getRowNodeId =
			(item) => {
				if (item[this.getIdField()]) {
					return item[this.getIdField()];
				} else {
					return null;
				}
			};

		this.gridOptions.rowData = this.values;
	}

	protected getColumnDefsWithOptions(): Array<any> {

		const colDefs: Array<any> = [
			{
				rowDrag: this.rowDrag,
				colId:   this.getIdField(),
				field:   this.getDescriptionField()
			}
		];

		if (this.multipleSelection && !this.hideChecks) {
			colDefs.unshift({
				colId:             'selectCol',
				headerName:        '',
				checkboxSelection: true,
				width:             this.getCheckColumnWidth(),
				suppressSizeToFit: true,
				resizable:         false,
				suppressMovable:   true
			});
		}
		this.addSuppressSizeToFitToColumnsWithWidthDefined(colDefs);

		return colDefs;
	}

	protected getCheckColumnWidth(): number {
		return 32;
	}

	public getAllFieldID(): number | string {
		return 0;
	}

	public getAllFieldDescription(): string {
		return 'All';
	}

	public cleanSelection() {
		this.unselectAllNodes();
	}

	protected addSuppressSizeToFitToColumnsWithWidthDefined(colDefs: ColDef[]) {
		colDefs.forEach(function(columnDef: ColDef) {
			if (columnDef.width) {
				columnDef.suppressSizeToFit = true;
			}
		});
	}

	public doGridReady(event: any) {
		this.gridOptions.api.sizeColumnsToFit();
		this.gridOptions.api.doLayout();
	}

	public doGridSizeChanged(event: any) {
		if (this.gridOptions.api) {
			this.gridOptions.api.sizeColumnsToFit();
		}
	}

	public doClick(row: any) {
		if (!this.multipleSelection && !this.isDisabled) {
			this.selectedItem = row.node.data;
			this.selectedItemChange.emit(row.node.data);
		}
	}

	// overrides
	public onRowSelected(event: any) {
		if (this.multipleSelection) {
			if (!this.isDisabled && event.node && event.node.data && event.node.data[this.getIdField()] !== undefined) {
				if (this.multipleSelectedItemList) {
					const elementIndexInSelectedList: number = this.multipleSelectedItemList.findIndex((item) => {
						return item[this.getIdField()] === event.node.data[this.getIdField()];
					});
					if (event.node.selected) {
						if (elementIndexInSelectedList < 0) {
							if (this.showAll) {
								if (event.node.data[this.getIdField()] === this.getAllFieldID()) {
									this.multipleSelectedItemList.push(event.node.data);
									this.unselectAllNodes();
								} else {
									const elementAllInSelectedList: number = this.multipleSelectedItemList.findIndex((item) => {
										return item[this.getIdField()] === this.getAllFieldID();
									});
									if (elementAllInSelectedList !== -1) {
										this.unselectNodeAll();
										this.multipleSelectedItemList = [];

									}
									this.multipleSelectedItemList.push(event.node.data);
									this.multipleSelectedItemList = this.multipleSelectedItemList.slice();
								}
							} else {
								this.multipleSelectedItemList.push(event.node.data);
								this.multipleSelectedItemList = this.multipleSelectedItemList.slice();
							}
						}
					} else {
						if (elementIndexInSelectedList !== -1) {
							this.multipleSelectedItemList.splice(elementIndexInSelectedList, 1);
							this.multipleSelectedItemList = this.multipleSelectedItemList.slice();
						}
					}
				} else {
					if (this.showAll && (event.node.data[this.getIdField()] === this.getAllFieldID())) {
						this.multipleSelectedItemList.push(event.node.data);
						this.unselectAllNodes();
					} else {
						this.multipleSelectedItemList = [];
						this.multipleSelectedItemList.push(event.node.data);
						this.multipleSelectedItemList = this.multipleSelectedItemList.slice();
					}
				}
			}
		}
	}

	public onModelUpdated(pEvent: any) {
		this.gridOptions.api.sizeColumnsToFit();
		this.selectItemInGrid();
		return pEvent;
	}

	protected selectItemInGrid(): void {
		if (this.gridOptions && this.gridOptions.api) {
			this.gridOptions.api.forEachNode(node => {
				if (node.data) {
					if (this.multipleSelection) {
						if (this.multipleSelectedItemList && this.multipleSelectedItemList.length > 0) {
							if (this.multipleSelectedItemList
								.filter((selectedItem) => {
									return (selectedItem !== undefined && selectedItem[this.getIdField()] === node.id);
								}).length > 0) {
								node.selectThisNode(true);
							}
						} else {
							node.selectThisNode(false);
						}
					} else {
						if (!this.selectedItem && this.selectFirstItem) {
							if (node.rowIndex === 0) {
								node.setSelected(true);
								this.selectedItem = node.data;
								this.selectedItemChange.emit(node.data);
								return;
							}
						} else if (this.selectedItem) {
							if (node.data[this.getIdField()] === this.selectedItem[this.getIdField()]) {
								node.setSelected(true);
								return;
							}
						}
					}
				}
			});
		}
	}

	private selectionItemListToIDList(): Array<string | number> {
		const idList = new Array<string | number>();
		for (const item of this.multipleSelectedItemList) {
			idList.push(item[this.getIdField()]);
		}
		return idList;
	}

	private unselectAllNodes() {
		if (this.gridOptions && this.gridOptions.api) {
			this.gridOptions.api.forEachNode(node => {
				if (node && node.id !== this.getAllFieldID()) {
					node.selectThisNode(false);
				}
			});
		}
	}

	private unselectNodeAll() {
		if (this.gridOptions && this.gridOptions.api) {
			this.gridOptions.api.forEachNode(node => {
				if (node && node.id === this.getAllFieldID()) {
					node.selectThisNode(false);
				}
			});
		}
	}

	public onRowDragEnd(event: any) {
		this.rowDragEnd.emit(event);
	}

}

