		if (files.length) {
			if (this.multipleSelection) {
				this.fileList = files;
				this.fileName = '';
				for (let index = 0; index < files.length; index++) {
					this.fileName += (this.fileName.length > 0 ? ', ' : '') + files[index].name;
				}
			} else {
				this.fileName = files[0].name;
				this.file = files[0];
			}
		}
	}
}

