import { Component } from '@angular/core';
import { MessagePopupButton } from './message-popup.service';
import { DialogRef } from '../dialog/dialog-ref';
import { ModalComponent, SystelabModalContext } from '../dialog/modal-context';
import { MessagePopupIcon } from './message-with-icon.component';

export class MessagePopupViewContext extends SystelabModalContext {

	public override fullScreen = false;
	public override maxWidth = 700;
	public override minWidth = 499;
	public override maxHeight = 400;
	public override minHeight = 280;
	public title: string;
	public msg: string;
	public icon: MessagePopupIcon;
	public type: number;
	public color: string;
	public buttons: MessagePopupButton[] = [];
}

@Component({
	selector:    'dialog-view',
	templateUrl: 'message-popup-view.component.html',
	styleUrls:   ['message-popup-view.component.scss']
})
export class MessagePopupViewComponent implements ModalComponent<MessagePopupViewContext> {

	public parameters: MessagePopupViewContext;
	public hasAnyButtonFocus = false;

	constructor(public dialog: DialogRef<MessagePopupViewContext>) {
		this.parameters = dialog.context;
		if (this.parameters.buttons) {
			this.hasAnyButtonFocus = this.parameters.buttons.some(button => button.focus);
		}
	}

	public static getParameters(): MessagePopupViewContext {
		return new MessagePopupViewContext();
	}

	public close(value?: any): void {
		this.dialog.close(value);
	}
}

import { Component, Input } from '@angular/core';

export class MessagePopupIcon {
	constructor(public cssClass: string, public cssPropertiesClass?: string) {

	}
}

@Component({
	selector: 'systelab-message-with-icon',
	templateUrl: 'message-with-icon.component.html',
	styleUrls: ['message-with-icon.component.scss']
})
export class MessageWithIconComponent {

	public static readonly MESSAGE_QUESTION = 1;
	public static readonly MESSAGE_ERROR = 2;
	public static readonly MESSAGE_WARNING = 3;
	public static readonly MESSAGE_INFO = 4;

	@Input() public message = '';
	@Input() public type = 1;
	@Input() public icon: MessagePopupIcon;

	public getIcon() {
		if (this.icon) {
			return this.icon.cssClass;
		} else {
			switch (this.type) {
				case MessageWithIconComponent.MESSAGE_QUESTION:
					return 'icon-question-circle';
				case MessageWithIconComponent.MESSAGE_ERROR:
					return 'icon-times-circle';
				case MessageWithIconComponent.MESSAGE_WARNING:
					return 'icon-warning';
				case MessageWithIconComponent.MESSAGE_INFO:
					return 'icon-info-circle';
				default:
					return '';
			}
		}
	}

	public getColor() {
		if (this.icon) {
			if (this.icon.cssPropertiesClass) {
				return this.icon.cssPropertiesClass;
			} else {
				return 'info';
			}
		} else {
			switch (this.type) {
				case MessageWithIconComponent.MESSAGE_QUESTION:
					return 'info';
				case MessageWithIconComponent.MESSAGE_ERROR:
					return 'danger';
				case MessageWithIconComponent.MESSAGE_WARNING:
					return 'warning';
				case MessageWithIconComponent.MESSAGE_INFO:
					return 'info';
				default:
					return 'info';
			}
		}
	}
}

