	selector: 'sample-route',
	template: `
<div style="padding-bottom: 10px;">
	<button type="button" class="btn" (click)="setColor('green')">Green</button>
	<button type="button" class="btn" (click)="setColor('red')">Red</button>
	<button type="button" class="btn" (click)="setColor('blue')">Blue</button>

	<button type="button" class="btn" (click)="save()">Save</button>
	<button type="button" class="btn" (click)="restore()">Restore</button>
	<button type="button" class="btn" (click)="clear()">Clear</button>

</div>
<div>
<canvas #canvas style="width: 100%; height: 100%;" 
				(mouseup)="doMouseUp($event)"
				(mousedown)="doMouseDown($event)"
				(mouseout)="doMouseOut($event)"
				(mousemove)="doMouseMove($event)" 
				[class.pointer]="mustShowPointer">

</canvas>
</div>
`
})
export class SampleRouteComponent implements OnInit, AfterViewInit {

	@ViewChild('canvas', {static: false}) public canvas: ElementRef;

	public mustShowPointer = false;

	@Input() public fixedWidth: number = null;
	@Input() public fixedHeight: number = null;

	public canvasWidth = 0;
	public canvasHeight = 0;

	public startID = 0;
	public startX = 0;
	public startY = 0;

	public isDown = false;
	public movingNode: RouteNode = null;

	public nodes: RouteNode[] = [];
	public lastNumber = 1;

	public connections: Connection[] = [];

	public img = new Image();

	public color = 'black';

	public ngOnInit() {
		this.img.src = 'data:image/png;base64,';
		this.img.onload = this.imageLoaded;

	}

	public ngAfterViewInit(): void {
		timer(50).subscribe(t => {
			this.initCanvasSize();
			this.restore();
		});
	}

	protected imageLoaded(event: any) {
		console.log(event);
	}

	public doMouseDown(event: MouseEvent) {
		event.stopPropagation();
		event.preventDefault();
		const mouseX = event.offsetX;
		const mouseY = event.offsetY;

		let selectedNode: RouteNode = null;
		this.movingNode = null;

		for (let i = 0; i < this.nodes.length && !selectedNode; i++) {
			if (mouseX > this.nodes[i].x && mouseX < this.nodes[i].x + this.nodes[i].w &&
				mouseY > this.nodes[i].y && mouseY < this.nodes[i].y + this.nodes[i].h) {
				selectedNode = this.nodes[i];
			}
		}

		if (!selectedNode) {
			this.nodes.push(new RouteNode(this.lastNumber++, mouseX - 15, mouseY - 15, 30, 30));
			this.drawModel();
		} else {
			this.startX = selectedNode.x + selectedNode.w / 2;
			this.startY = selectedNode.y + selectedNode.h / 2;
			this.startID = selectedNode.id;
			this.isDown = true;
			if (!event.altKey) {
				this.movingNode = selectedNode;
			}
		}
	}

	public doMouseMove(event: MouseEvent) {
		if (this.isDown) {
			const mouseX = event.offsetX;
			const mouseY = event.offsetY;

			event.stopPropagation();
			event.preventDefault();
			if (this.movingNode) {
				this.movingNode.x = mouseX - this.movingNode.w / 2;
				this.movingNode.y = mouseY - this.movingNode.h / 2;
				this.drawModel();

			} else {
				this.drawModel(mouseX, mouseY);
			}
		}
	}

	public doMouseUp(event: MouseEvent) {
		event.stopPropagation();
		event.preventDefault();
		const mouseX = event.offsetX;
		const mouseY = event.offsetY;

		if (this.isDown) {

			if (!this.movingNode) {
				let selectedNode: RouteNode = null;
				for (let i = 0; i < this.nodes.length && !selectedNode; i++) {
					if (mouseX > this.nodes[i].x && mouseX < this.nodes[i].x + this.nodes[i].w &&
						mouseY > this.nodes[i].y && mouseY < this.nodes[i].y + this.nodes[i].h) {
						selectedNode = this.nodes[i];
					}
				}
				if (selectedNode) {
					this.connections.push(new Connection(this.startID, selectedNode.id));

				} else {
					this.drawModel();
				}
			}
			this.isDown = false;
		}
	}

	public doMouseOut(event: MouseEvent) {

	}

	private drawModel(toX: number = null, toY: number = null) {
		// clear the canvas
		const context: CanvasRenderingContext2D = this.canvas.nativeElement.getContext('2d');

		context.beginPath();
		context.fillStyle = 'lightGray';
		context.strokeStyle = 'black';

		context.clearRect(0, 0, this.canvasWidth, this.canvasHeight);
		context.fillRect(0, 0, this.canvasWidth, this.canvasHeight);

		context.rect(0, 0, this.canvasWidth, this.canvasHeight);
		context.stroke();
		context.closePath();

		// redraw all previous lines
		for (let i = 0; i < this.connections.length; i++) {
			const nodeFrom = this.getNodeById(this.connections[i].idFrom);
			const nodeTo = this.getNodeById(this.connections[i].idTo);

			this.drawLine(context, {
				x1: nodeFrom.x + nodeFrom.w / 2,
				y1: nodeFrom.y + nodeFrom.h / 2,
				x2: nodeTo.x + nodeTo.w / 2,
				y2: nodeTo.y + nodeTo.h / 2,
			});
		}

		// redraw all previous nodes
		for (let i = 0; i < this.nodes.length; i++) {
			this.drawNode(context, this.nodes[i]);
		}

		if (toX && toY) {

			// draw the current line
			this.drawLine(context, {
				x1: this.startX,
				y1: this.startY,
				x2: toX,
				y2: toY
			});
		}
	}

	private getNodeById(id: number): RouteNode {
		for (let i = 0; i < this.nodes.length; i++) {
			if (this.nodes[i].id === id) {
				return this.nodes[i];
			}
		}
		return null;
	}

	private drawLine(context: CanvasRenderingContext2D, line: any) {
		context.lineWidth = 2;
		context.strokeStyle = this.color;
		context.beginPath();
		context.moveTo(line.x1, line.y1);
		context.lineTo(line.x2, line.y2);
		context.stroke();
		context.closePath();
	}

	private drawNode(context: CanvasRenderingContext2D, node: any) {
		context.drawImage(this.img, node.x, node.y);
	}

	@HostListener('window:resize', ['$event'])
	public onResize(event: any) {
		this.initCanvasSize();
		this.drawModel();
	}

	public initCanvasSize() {
		if (this.fixedWidth) {
			this.canvas.nativeElement.width = this.fixedWidth;
		} else {
			this.canvas.nativeElement.width = this.canvas.nativeElement.offsetWidth;
		}

		if (this.fixedHeight) {
			this.canvas.nativeElement.height = this.fixedHeight;
		} else {
			this.canvas.nativeElement.height = this.canvas.nativeElement.offsetHeight;
		}

		this.canvasWidth = this.canvas.nativeElement.width;
		this.canvasHeight = this.canvas.nativeElement.height;

	}

	public setColor(color: string) {
		this.color = color;
		this.drawModel();
	}

	public save() {
	}

	public restore() {
		this.clear();
		this.setColor('black');
		this.nodes.push(new RouteNode(1, 100, 150, 30, 30));
		this.nodes.push(new RouteNode(2, 300, 150, 30, 30));
		this.nodes.push(new RouteNode(3, 200, 250, 30, 30));
		this.nodes.push(new RouteNode(4, 300, 350, 30, 30));
		this.connections.push(new Connection(1, 3));
		this.connections.push(new Connection(3, 4));
		this.connections.push(new Connection(3, 2));
		this.lastNumber = 5;
		this.drawModel();
	}

	public clear() {
		this.connections = [];
		this.nodes = [];
		this.drawModel();
	}
}

import { Component, ViewChild } from '@angular/core';
import { SearcherTableComponent } from './searcher.table.component';
import { I18nService } from 'systelab-translate';
import { ModalComponent } from '../modal/dialog/modal-context';
import { DialogRef } from '../modal/dialog/dialog-ref';
import { SearcherDialogParameters } from './searcher.dialog.parameters';

@Component({
	templateUrl: 'searcher.dialog.component.html'
})
export class SearcherDialog<T> implements ModalComponent<SearcherDialogParameters<T>> {

	@ViewChild(SearcherTableComponent, {static: false}) public tableComponent: SearcherTableComponent<T>;
	public parameters: SearcherDialogParameters<T>;
	public searchingValue: string;

	public searchLabel: string;
	public titleForDialog: string;
	public multipleSelection = false;
	public showClose: boolean;

	public searchByContains = false;

	constructor(public dialog: DialogRef<SearcherDialogParameters<T>>, protected i18nService: I18nService) {
		this.parameters = dialog.context;
		this.searchingValue = this.parameters.valueToSearch;

		this.showClose = this.parameters.showCloseButton;
		if (!this.parameters.showCloseButton) {
			this.parameters.isBlocking = true;
			this.parameters.keyboard = null;
		}

		if (this.parameters.searcher) {
			this.searchLabel = this.parameters.searcher.getTextForSearcherLabel();
			this.titleForDialog = this.parameters.searcher.getTitleForDialog();
			this.multipleSelection = this.parameters.searcher.multipleSelection;
		}

	}

	public getPlaceHolder() {
		if (this.searchByContains) {
			return this.i18nService.instant('COMMON_CONTAINS');
		} else {
			return this.i18nService.instant('COMMON_STARTS_WITH');
		}
	}

	public close(): void {
		this.dialog.close();
	}

	public submit(): void {
		if (this.parameters.searcher.multipleSelection) {
			this.dialog.close(this.parameters.searcher.multipleSelectedItemList);
		} else {
			this.dialog.close(this.tableComponent.getSelectedElements());
		}
	}

	public refreshSearch(): void {
		this.tableComponent.refreshTable();
	}

	public selectOnClick(data: T) {
		if (!this.multipleSelection) {
			if (data) {
				const arr: Array<T> = new Array<T>();
				arr.push(data);
				this.dialog.close(arr);
			}
		}
	}
}

import { Component, Input, OnInit } from '@angular/core';
import { PreferencesService } from 'systelab-preferences';
import { I18nService } from 'systelab-translate';
import { RowNode } from 'ag-grid-community';
import { AbstractSearcher } from './abstract-searcher';
import { AbstractApiGrid } from '../grid/abstract-api-grid.component';
import { DialogService } from '../modal/dialog/dialog.service';
import { Observable } from 'rxjs';

@Component({
	selector:    'systelab-internal-searcher-table',
	templateUrl: '../grid/abstract-grid.component.html'

})
export class SearcherTableComponent<T> extends AbstractApiGrid<T> implements OnInit {

	@Input() public valueForSearch: string;
	@Input('contains') public searchForContain: boolean;
	@Input() public searcher: AbstractSearcher<T>;

	constructor(protected override preferencesService: PreferencesService, protected override i18nService: I18nService,
	            protected override dialogService: DialogService) {
		super(preferencesService, i18nService, dialogService);
	}

	public override ngOnInit(): void {
		super.ngOnInit();
		this.gridOptions.enableBrowserTooltips = true;
	}

	protected getColumnDefs(): Array<any> {
		return this.searcher.getColumnDefs();
	}

	protected override hideHeader(): boolean {
		return this.searcher.hideHeader();
	}

	protected override getIsFullWidthCell(rowNode: RowNode): boolean {
		return this.searcher.getIsFullWidthCell(rowNode);
	}

	public override getFullWidthCellRenderer(): any {
		return this.searcher.getFullWidthCellRenderer();
	}

	protected getContextMenuOptions(): Array<any> {
		return new Array<any>();
	}

	public getTotalItems() {
		return this.searcher.getTotalItems();
	}

	protected getData(page: number, itemsPerPage: number): Observable<Array<T>> {
		let searchText: string;
		if (this.valueForSearch) {
			searchText = (this.searchForContain) ? '%' + this.valueForSearch : this.valueForSearch;
		}
		return this.searcher.getData(searchText, page, itemsPerPage);
	}

	public refreshTable(): void {
		this.refresh();
	}

	public getSelectedElements(): Array<T> {
		return this.gridOptions.api.getSelectedRows();
	}

	protected override getGridOptionsPreferencesPrefix(): string {
		return this.searcher.getGridOptionsPreferencesPrefix();
	}

	public override onModelUpdated(event: any) {
		super.onModelUpdated(event);
		if (this.multipleSelection) {
			if (this.searcher && this.searcher.multipleSelectedItemList && this.searcher.multipleSelectedItemList.length > 0) {
				this.gridOptions.api.forEachNode(node => {
					if (this.searcher.multipleSelectedItemList
						.filter((selectedItem) => {
							return (selectedItem && node.data && selectedItem[this.searcher.getCodeField()] === node.data[this.searcher.getCodeField()]);
						}).length > 0) {
						node.selectThisNode(true);
					}
				});
			}
		} else if (this.searcher && this.searcher.id && this.searcher.id !== undefined) {
			this.gridOptions.api.forEachNode(node => {
				if (node.data && node.data[this.searcher.getIdField()] === this.searcher.id) {
					node.selectThisNode(true);
				}
			});
		}
	}

	// overrides
	public override onRowSelected(event: any): void {
		if (this.multipleSelection) {
			if (event.node && event.node.data && event.node.data[this.searcher.getIdField()] !== undefined) {
				if (this.searcher.multipleSelectedItemList) {
					const element = this.searcher.multipleSelectedItemList.find((item) => {
						return item[this.searcher.getCodeField()] === event.node.data[this.searcher.getCodeField()];
					});
					if (event.node.selected && !element) {
						this.addElementToMultipleSelectedItemList(event.node.data);
					} else if (!event.node.selected && element) {
						this.searcher.multipleSelectedItemList = this.searcher.multipleSelectedItemList
							.filter((item) => item[this.searcher.getCodeField()] !== element[this.searcher.getCodeField()]);
					}
				} else {
					this.addElementToMultipleSelectedItemList(event.node.data);
				}
			}
		}
	}

	private addElementToMultipleSelectedItemList(element: T): void {
		if (!this.searcher.multipleSelectedItemList) {
			this.searcher.multipleSelectedItemList = [];
		}
		this.searcher.multipleSelectedItemList.push(element);
		this.searcher.multipleSelectedItemList = this.searcher.multipleSelectedItemList.slice();
	}

}

